{%- liquid 
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign width_content = section.settings.width_content
  assign full_width = section.settings.full_width

  assign content_type = section.settings.content_type

  assign text = section.settings.text
  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_color = section.settings.text_color
  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_height = section.settings.text_height

  assign image = section.settings.image
  assign image_height = section.settings.image_height
  assign image_height_mobile = section.settings.image_height_mobile

-%}

{%- style -%}

  {{  text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
  }

  .top-banner-{{ section.id }} {
    transition: height 0.3s;
  }

  .top-banner-text-{{ section.id }} {
    margin: 0;
    text-align: center;
    font-size: {{ text_size_mobile }}vw;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .top-banner-image-{{ section.id }} {
    width: 100%;
    height: {{ image_height_mobile }}px;
  }

  .top-banner-image-{{ section.id }} img,
  .top-banner-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .top-banner-image-{{ section.id }} svg {
    background-color: #FAFAFA;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
    }

    .top-banner-text-{{ section.id }} {
      font-size: {{ text_size }}vw;
    }

    .top-banner-image-{{ section.id }} {
      height: {{ image_height }}px;
    }

  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ width_content }}%;
    }
  </style>
{% endunless %}

{% if text_custom %}
  <style>
    .top-banner-text-{{ section.id }} {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

<template id="top-banner-template-{{ section.id }}">
  <div class="section-{{ section.id }} top-banner-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
      <div class="section-{{ section.id }}-settings">
        {% if content_type == "text" %}
        {% if text != blank %}
            <p class="top-banner-text-{{ section.id }}">{{ text }}</p>
          {% endif %}
        {% else %}
          <div class="top-banner-image-{{ section.id }}">
          {% if image != blank %}
            <img src="{{ image | image_url }}" alt="{{ image.alt }}">
          {% else %}
            {{ 'lifestyle-2' | placeholder_svg_tag }}
          {% endif %}
          </div>
        {% endif %}
      </div>
  </div>
</template>

<div id="top-banner-local-target-{{ section.id }}"></div>

<script>
  function initTopBannerText() {
    let cssTarget = '{{ section.settings.insert_css_class_target }}';
    let productForm = cssTarget
      ? document.querySelector(cssTarget)
      : document.querySelector('#top-banner-local-target-{{ section.id }}');

    if (!productForm) return;

    const templateContent = document.querySelector(
      '#top-banner-template-{{ section.id }}'
    ).content;

    productForm.insertAdjacentElement(
      'beforebegin',
      templateContent.firstElementChild
    );

    setTimeout(() => {
      let height = document.querySelector('.top-banner-{{ section.id }}').offsetHeight;
      window.addEventListener('scroll', () => {
        if ((document.body.scrollTop < 20 && document.documentElement.scrollTop < 20) || window.scrollY < 20) {
          document.querySelector('.top-banner-{{ section.id }}').style.height = height + "px";
        } else {
          document.querySelector('.top-banner-{{ section.id }}').style.height = '0';
        }
      });
    }, 400);
  }

  document.addEventListener('DOMContentLoaded', initTopBannerText);

</script>
