{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign banner_radius = section.settings.banner_radius

  assign banner_height = section.settings.banner_height
  assign banner_fixed_height = section.settings.banner_fixed_height
  assign banner_fixed_height_mobile = section.settings.banner_fixed_height_mobile

  assign image = section.settings.image
  assign image_mobile = section.settings.image_mobile

  assign overlay = section.settings.overlay
  assign overlay_color = section.settings.overlay_color

  assign content_maxwidth = section.settings.content_maxwidth
  assign content_vertical_position_mobile = section.settings.content_vertical_position_mobile
  assign content_vertical_position = section.settings.content_vertical_position
  assign content_horizontal_position = section.settings.content_horizontal_position

  assign modal_radius = section.settings.modal_radius
  assign modal_border_thickness = section.settings.modal_border_thickness
  assign modal_width = section.settings.modal_width
  assign modal_overlay = section.settings.modal_overlay
  assign modal_border_color = section.settings.modal_border_color
  assign modal_overlay_color = section.settings.modal_overlay_color
  assign modal_overlay = modal_overlay | times: 0.01

  assign close_color = section.settings.close_color

-%}

{%- style -%}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    position: relative;
    overflow: hidden;
    border-radius: {{ banner_radius }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    position: relative;
    z-index: 2;
    display: flex;
    height: 100%;
    align-items: {{ content_vertical_position_mobile }};
  }

  .banner-{{ section.id }}:after {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background-color: {{ overlay_color | hex_to_rgba: overlay }};
    z-index: 1;
  }

  .banner-bg-{{ section.id }} {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 1;
  }

  .banner-bg-{{ section.id }} img,
  .banner-bg-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .banner-bg-{{ section.id }} svg {
     background-color: #AFAFAF;
  }

  .banner-content-{{ section.id }} {
    width: 100%;
  }

  .banner-modal-{{ section.id }} {
    width: 100vw;
    height: 100vh;
    top: 0;
    left: 0;
    position: fixed;
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    overflow-y: scroll;
    pointer-events: none;
    background: {{ modal_overlay_color | hex_to_rgba: modal_overlay }};
    padding: 10px;
  }

  .banner-modal-{{ section.id }}.active {
    opacity: 1;
    z-index: 100;
    pointer-events: all;
    transition: all 250ms ease-in 0s;
  }

  .banner-modal-inner-{{ section.id }} {
    position: relative;
    width: 100%;
    max-height: 96vh;
    border: {{ modal_border_thickness }}px solid {{ modal_border_color }};
    border-radius: {{ modal_radius }}px;
    overflow: hidden;
  }

  .banner-modal-inner-{{ section.id }} video,
  .banner-modal-inner-{{ section.id }} iframe {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    box-shadow: none;
    border: 0px;
  }

  .banner-modal-inner-{{ section.id }} iframe {
    min-height: 70vh;
  }

  .banner-close-{{ section.id }} {
    position: absolute;;
    top: 10px;
    right: 8px;
    box-shadow: none;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
    z-index: 2;
  }

  .banner-close-{{ section.id }} svg {
    display: block;
    width: 30px;
    height: 30px;
  }

  .banner-close-{{ section.id }} svg path {
    fill: {{ close_color }};
  }

  .portrait-video-{{ section.id }} {
    max-width: 100%;
    margin: 0px auto;
  }

  .landspace-video-{{ section.id }} {
    max-width: 100%;
    margin: 0px auto;
  }

  .image-desktop-{{ section.id }} {
    display: none !important;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
      align-items: {{ content_vertical_position }};
      justify-content: {{ content_horizontal_position }};
    }

    .banner-content-{{ section.id }} {
      max-width: {{ content_maxwidth }}%;
    }

    .banner-modal-inner-{{ section.id }} {
      max-width: {{ modal_width }}%;
    }

    .portrait-video-{{ section.id }} {
      max-width: 40%;
    }

    .landspace-video-{{ section.id }} {
      max-width: 80%;
    }

    .image-desktop-{{ section.id }} {
      display: block !important;
    }

    .image-mobile-{{ section.id }} {
      display: none !important;
    }
  }

{%- endstyle -%}

{% if banner_height == "adapt_first" %}
  <style>
    .banner-{{ section.id }} {
      height: 100vh;
    }
  </style>
{% elsif banner_height == "auto_height" %}
  <style>

   .banner-{{ section.id }} {
      height: auto;
    }

  </style>
{% else %}
  <style>
    .banner-{{ section.id }} {
      height: {{ banner_fixed_height_mobile }}px;
    }

    @media(min-width: 768px) {
      .banner-{{ section.id }} {
        height: {{ banner_fixed_height }}px;
      }
    }
  </style>
{% endif %}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% for block in section.blocks %}
  {% if block.type == "heading" %}
    <style>
      {{  block.settings.heading_font | font_face: font_display: 'swap' }}

      .banner-heading-{{ block.id }} {
        text-align: {{ block.settings.heading_align_mobile }};
        margin-top: {{ block.settings.heading_mt_mobile }}px;
      }

      .banner-heading-{{ block.id }} * {
        margin: 0;
        font-size: {{ block.settings.heading_size_mobile }}px;
        color: {{ block.settings.heading_color }};
        line-height: {{ block.settings.heading_height }}%;
        text-transform: unset;
      }

      @media(min-width: 1024px) {

        .banner-heading-{{ block.id }} {
          text-align: {{ block.settings.heading_align }};
          margin-top: {{ block.settings.heading_mt }}px;
        }

        .banner-heading-{{ block.id }} * {
          font-size: {{ block.settings.heading_size }}px;
        }
      }
    </style>

    {% if block.settings.heading_custom %}
      <style>
        .banner-heading-{{ block.id }} * {
          font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
          font-weight: {{ block.settings.heading_font.weight }};
          font-style: {{ block.settings.heading_font.style }};
        }
      </style>
    {% endif %}

  {% elsif block.type == "text" %}
    <style>
      {{  block.settings.text_font | font_face: font_display: 'swap' }}

      .banner-text-{{ block.id }} {
        text-align: {{ block.settings.text_align_mobile }};
        margin-top: {{ block.settings.text_mt_mobile }}px;
      }

      .banner-text-{{ block.id }} * {
        margin: 0;
        font-size: {{ block.settings.text_size_mobile }}px;
        color: {{ block.settings.text_color }};
        line-height: {{ block.settings.text_height }}%;
        text-transform: unset;
      }

      @media(min-width: 1024px) {

        .banner-text-{{ block.id }} {
          text-align: {{ block.settings.text_align }};
          margin-top: {{ block.settings.text_mt }}px;
        }

        .banner-text-{{ block.id }} * {
          font-size: {{ block.settings.text_size }}px;
        }
      }
    </style>

    {% if block.settings.text_custom %}
      <style>
        .banner-text-{{ block.id }} {
          font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
          font-weight: {{ block.settings.text_font.weight }};
          font-style: {{ block.settings.text_font.style }};
        }
      </style>
    {% endif %}

  {% elsif block.type == "button" %}

  <style>
    .banner-button-{{ block.id }} {
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 10px;
      width: 100%;
      max-width: {{ block.settings.button_width_mobile }}px;
      margin: 0;
      margin-top: {{ block.settings.button_mt_mobile }}px;
      font-size: {{ block.settings.button_size_mobile }}px;
      color: {{ block.settings.button_color }};
      line-height: {{ block.settings.button_height }}%;
      text-align: center;
      text-transform: unset;
      text-decoration: none;
      padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
      border-radius: {{ block.settings.button_radius }}px;
      transition: all 0.25s ease 0s;
      position: relative;
      font-weight: 700;
      z-index: 2;
    }

    .banner-button-{{ block.id }}:hover {
      color: {{ block.settings.button_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .banner-button-{{ block.id }} svg {
      width: 14px;
      height: 14px;
    }

    .banner-button-{{ block.id }} svg path {
      fill: {{ block.settings.button_color }};
      transition: all 0.25s ease 0s;
    }

    .banner-button-{{ block.id }}:hover svg path {
      fill: {{ block.settings.button_hover_color }};
      transition: all 0.25s ease 0s;
    }

    @media(min-width: 1024px) {
      .banner-button-{{ block.id }} {
        margin-top: {{ block.settings.button_mt }}px;
        max-width: {{ block.settings.button_width }}px;
        padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
        font-size: {{ block.settings.button_size }}px;
      }
    }
  </style>

  {% if block.settings.button_custom %}
    <style>
      .banner-button-{{ block.id }} {
        font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
        font-weight: {{ block.settings.button_font.weight }};
        font-style: {{ block.settings.button_font.style }};
      }
    </style>
  {% endif %}

  {% if block.settings.button_align_mobile == "center" %}
    <style>
     .banner-button-{{ block.id }} {
        margin-left: auto;
        margin-right: auto;
      } 
    </style>
  {% elsif block.settings.button_align_mobile == "right" %}
    <style>
     .banner-button-{{ block.id }} {
        margin-left: auto;
      } 
    </style>
  {% endif %}

  {% if block.settings.button_align == "center" %}
    <style>
      @media(min-width: 1024px) {
       .banner-button-{{ block.id }} {
          margin-left: auto !important;
          margin-right: auto !important;
        } 
      }
    </style>
  {% elsif block.settings.button_align == "right" %}
    <style>
      @media(min-width: 1024px) {
       .banner-button-{{ block.id }} {
          margin-left: auto !important;
          margin-right: 0px !important;
        } 
      }
    </style>
  {% else %}
    <style>
      @media(min-width: 1024px) {
       .banner-button-{{ block.id }} {
          margin-left: 0px !important;
          margin-right: 0px !important;
        } 
      }
    </style>
  {% endif %}

  {% if block.settings.button_style == "non_outline" or block.settings.button_style == "non_outline_arrow" %}
    <style>
      .banner-button-{{ block.id }} {
        background-color: {{ block.settings.button_bg_color }};
      }

      .banner-button-{{ block.id }}:hover {
        background-color: {{ block.settings.button_bg_hover_color }};
      }
    </style>
  {% elsif block.settings.button_style == "outline" or block.settings.button_style == "outline_arrow" %}
    <style>
      .banner-button-{{ block.id }} {
        background-color: {{ block.settings.button_bg_color }};
        border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_color }};
      }

      .banner-button-{{ block.id }}:hover {
        background-color: {{ block.settings.button_bg_hover_color }};
        border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_hover_color }};
      }
    </style>
  {% endif %}

  {% elsif block.type == "video" %}

    <style>

      {{  block.settings.toggle_text_font | font_face: font_display: 'swap' }}

      .banner-toggle-{{ block.id }} {
        display: flex;
        margin-top: {{ block.settings.video_mt_mobile }}px;
        max-width: fit-content;
        align-items: center;
        gap: 28px;
        cursor: pointer;
      }

      .banner-play-{{ section.id }} {
        flex: 0 0 {{ block.settings.icon_size_mobile }}px;
        height: {{ block.settings.icon_size_mobile }}px;
      }

      .banner-play-{{ section.id }} svg {
        display: block;
        width: 100%;
        height: 100%;
        object-fit: cover;
      }

      @keyframes pulse {
        0% {
          transform: scale(1);
        }
        50% {
          transform: scale(1.1);
        }
        100% {
          transform: scale(1);
        }
      }

      .banner-toggle-{{ block.id }}:hover .banner-play-{{ section.id }} {
        animation: pulse 1s infinite;
      }

      .banner-toggle-text-{{ block.id }} {
        margin: 0px;
        font-size: {{ block.settings.toggle_text_size_mobile }}px;
        color: {{ block.settings.toggle_text_color }};
        line-height: {{ block.settings.toggle_text_height }}%;
        text-transform: unset;
      }

      .banner-toggle-{{ block.id }}:hover .banner-toggle-text-{{ block.id }} {
        text-decoration: underline;
      }

      @media(min-width: 1024px) {

        .banner-toggle-{{ block.id }} {
          margin-top: {{ block.settings.video_mt }}px;
        }

        .banner-play-{{ section.id }} {
          flex: 0 0 {{ block.settings.icon_size }}px;
          height: {{ block.settings.icon_size }}px;
        }

        .banner-toggle-text-{{ block.id }} {
          font-size: {{ block.settings.toggle_text_size }}px;
        }
      }
    </style>

    {% if block.settings.toggle_text_custom %}
      <style>
        .banner-toggle-text-{{ block.id }} {
          font-family: {{ block.settings.toggle_text_font.family }}, {{ block.settings.toggle_text_font.fallback_families }};
          font-weight: {{ block.settings.toggle_text_font.weight }};
          font-style: {{ block.settings.toggle_text_font.style }};
        }
      </style>
    {% endif %}

    {% if block.settings.toggle_align_mobile == "center" %}
      <style>
       .banner-toggle-{{ block.id }} {
          margin-left: auto;
          margin-right: auto;
        } 
      </style>
    {% elsif block.settings.toggle_align_mobile == "right" %}
      <style>
       .banner-toggle-{{ block.id }} {
          margin-left: auto;
        } 
      </style>
    {% endif %}

    {% if block.settings.toggle_align == "center" %}
      <style>
        @media(min-width: 1024px) {
         .banner-toggle-{{ block.id }} {
            margin-left: auto !important;
            margin-right: auto !important;
          } 
        }
      </style>
    {% elsif block.settings.toggle_align == "right" %}
      <style>
        @media(min-width: 1024px) {
         .banner-toggle-{{ block.id }} {
            margin-left: auto !important;
            margin-right: 0px !important;
          } 
        }
      </style>
    {% else %}
      <style>
        @media(min-width: 1024px) {
         .banner-toggle-{{ block.id }} {
            margin-left: 0px !important;
            margin-right: 0px !important;
          } 
        }
      </style>
    {% endif %}

  {% endif %}

{% endfor %}

<div class="section-{{ section.id }} banner-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">

    <div class="banner-bg-{{ section.id }}">
      {% if image != blank %}
        <img src="{{ image | image_url }}" alt="{{ image.alt }}" {% if lazy %}loading="lazy"{% endif %} {% if image_mobile != blank %}class="image-desktop-{{ section.id }}"{% endif %}>
      {% else %}
        {{ 'image' | placeholder_svg_tag }}
      {% endif %}

      {% if image_mobile != blank %}
        <img src="{{ image_mobile | image_url }}" alt="{{ image_mobile.alt }}" {% if lazy %}loading="lazy"{% endif %} class="image-mobile-{{ section.id }}">
      {% endif %}

    </div>

    <div class="section-{{ section.id }}-settings">
      <div class="banner-content-{{ section.id }}">
        {% for block in section.blocks %}
          {% if block.type == "heading" %}
            <div class="banner-heading-{{ block.id }}">
              {{ block.settings.heading }}
            </div>
          {% endif %}
          {% if block.type == "text" %}
            <div class="banner-text-{{ block.id }}">
              {{ block.settings.text }}
            </div>
          {% endif %}

          {% if block.type == "button" %}
            <a href="{{ block.settings.button_url }}" class="banner-button-{{ block.id }}">
              {{ block.settings.button }}
