                </svg>
              {% endif %}
            </a>
          {% elsif block.type == "video" %}
            <div class="banner-toggle-{{ section.id }} banner-toggle-{{ block.id }}" id="{{ block.id }}">
              <div class="banner-play-{{ section.id }}">
                <svg xmlns="http://www.w3.org/2000/svg" aria-hidden="true" focusable="false" class="icon icon-play-circle" fill="none" viewBox="0 0 44 44">
                  <path fill-rule="evenodd" clip-rule="evenodd" d="M22 44C34.1503 44 44 34.1503 44 22C44 9.84974 34.1503 0 22 0C9.84974 0 0 9.84974 0 22C0 34.1503 9.84974 44 22 44ZM29.9286 22.8659C30.5953 22.481 30.5953 21.5187 29.9286 21.1338L18.7857 14.7005C18.1191 14.3156 17.2857 14.7967 17.2857 15.5665V28.4332C17.2857 29.203 18.1191 29.6841 18.7857 29.2992L29.9286 22.8659Z" fill="{{ block.settings.toggle_icon_color }}"></path>
                </svg>
              </div>
              {% if block.settings.toggle_text != blank %}
                <p class="banner-toggle-text-{{ block.id }}">{{ block.settings.toggle_text }}</p>
              {% endif %}
            </div>
          {% endif %}
        {% endfor %}
      </div>
    </div>
</div>

{% for block in section.blocks %}
  {% if block.type == "video" %}
    <div class="banner-modal-{{ section.id }}" data-id="{{ block.id }}">
      <button class="banner-close-{{ section.id }}">
        <svg width="24" height="24" viewBox="0 0 24 24" fill="none"><path fill-rule="evenodd" clip-rule="evenodd" d="M12.0002 10.586L4.70718 3.29297L3.29297 4.70718L10.586 12.0002L3.29297 19.2933L4.70718 20.7075L12.0002 13.4145L19.2933 20.7075L20.7075 19.2933L13.4145 12.0002L20.7075 4.70723L19.2933 3.29302L12.0002 10.586Z" fill="white"></path></svg>
      </button>
      <div class="banner-modal-inner-{{ section.id }}">
        {% assign video_id = block.settings.video.id | default: block.settings.video_url.id %}
        {%- if block.settings.video == null and block.settings.video_url != null -%}

          {% assign loop = "" %}

          {% if block.settings.loop %}
            {% assign loop = '&loop=1&playlist=' | append: video_id %}
          {% endif %}

          {% assign autoplay = '&autoplay=0' %}
          {% assign autoplay2 = '&autoplay=1' %}

          {%- if block.settings.video_url.type == 'youtube' -%}
            <iframe
              data-src="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1{{ autoplay }}{{ loop }}"
              data-src1="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1{{ autoplay2 }}{{ loop }}"
              data-src2="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1{{ autoplay }}{{ loop }}"
              class="js-youtube"
              allow="autoplay; encrypted-media"
              allowfullscreen
              title="Fixed Video"
            ></iframe>
          {%- else -%}
            <iframe
              data-src="https://player.vimeo.com/video/{{ video_id }}{{ autoplay }}{{ loop }}"
              data-src1="https://player.vimeo.com/video/{{ video_id }}{{ autoplay2 }}{{ loop }}"
              data-src2="https://player.vimeo.com/video/{{ video_id }}{{ autoplay }}{{ loop }}"
              class="js-vimeo"
              allow="autoplay; encrypted-media"
              allowfullscreen
              title="Fixed Video"
            ></iframe>
          {% endif %}
        {% else %}

          {% assign width_video = block.settings.video.sources[0].width %}
          {% assign height_video = block.settings.video.sources[0].height %}
          <div class="banner-modal-inner-video-{{ section.id }} {% if height_video > width_video %}portrait-video-{{ section.id }}{% else %}landscape-video-{{ section.id }}{% endif %}">
            <video
              data-src="{{ block.settings.video.sources[1].url }}"
              playsinline
              preload="none"
              {% if block.settings.loop %}loop{% endif %}
              {% if block.settings.controls %}controls{% endif %}
              class="lazy"
            ></video>
          </div>
        {% endif %}
      </div>
    </div>
  {% endif %}
{% endfor %}

<script src="https://cdn.jsdelivr.net/npm/vanilla-lazyload@19.1.3/dist/lazyload.min.js"></script> 

<script>
  function initVideoBanner3() {

    var lazyLoadInstance = new LazyLoad();

    window.addEventListener(
      "LazyLoad::Initialized",
      function (event) {
        window.lazyLoadInstance = event.detail.instance;
      },
      false
    );

    let btns = document.querySelectorAll('.banner-toggle-{{ section.id }}');
    let modals = document.querySelectorAll('.banner-modal-{{ section.id }}');

    if(btns) {
      btns.forEach(el => {
        el.addEventListener('click', () => {
          if(modals) {
            modals.forEach(element => {
              let dataId = element.getAttribute('data-id');
              let video = element.querySelector('video');
              let frame = element.querySelector('iframe');
              if(el.id == dataId) {
                element.classList.add('active');
                document.body.style.overflow = 'hidden';

                if(video) {
                  setTimeout(() => {
                    video.play();
                  }, 300);
                }

                if(frame) {
                  let dataSrc = frame.getAttribute('data-src1');
                  setTimeout(() => {
                    frame.src = dataSrc;
                  }, 300);
                }
              }
            })
          }
        })
      })
    }

    if(modals) {
      modals.forEach(el => {
        let video = el.querySelector('video');
        let frame = el.querySelector('iframe');
        let close = el.querySelector('.banner-close-{{ section.id }}');

        if(close) {
           close.addEventListener('click', () => {
            document.body.style.overflow = '';
            el.classList.remove('active');

            if(video) {
              video.pause();
            }

            if(frame) {
              let dataSrc2 = frame.getAttribute('data-src2');
              frame.src = dataSrc2;
            }
          }); 
        }

        document.addEventListener('click', (e) => {
            if (e.target == el) {
              document.body.style.overflow = '';
              el.classList.remove('active');

              if(video) {
                video.pause();
              }

              if(frame) {
                let dataSrc2 = frame.getAttribute('data-src2');
                frame.src = dataSrc2;
              }
            }
        });
      })
    }

  }

  document.addEventListener('DOMContentLoaded', initVideoBanner3);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initVideoBanner3);
     document.addEventListener('shopify:section:load', initVideoBanner3);
  }
</script>
