{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy

  assign grid_gap = section.settings.grid_gap
  assign grid_gap_mobile = section.settings.grid_gap_mobile
  assign grid_bg_color = section.settings.grid_bg_color
  assign grid_border_color = section.settings.grid_border_color
  assign grid_padding_vertical = section.settings.grid_padding_vertical
  assign grid_padding_horizontal = section.settings.grid_padding_horizontal
  assign grid_radius = section.settings.grid_radius
  assign grid_border_thickness = section.settings.grid_border_thickness
  assign grid_shadow = section.settings.grid_shadow
  assign grid_shadow_color = section.settings.grid_shadow_color

  assign media_position_mobile = section.settings.media_position_mobile
  assign media_position = section.settings.media_position

  assign content_bg_color = section.settings.content_bg_color
  assign content_border_color = section.settings.content_border_color
  assign content_padding_vertical = section.settings.content_padding_vertical
  assign content_padding_horizontal = section.settings.content_padding_horizontal
  assign content_radius = section.settings.content_radius
  assign content_border_thickness = section.settings.content_border_thickness

  assign heading = section.settings.heading
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_color = section.settings.heading_color
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height
  assign heading_align = section.settings.heading_align
  assign heading_align_mobile = section.settings.heading_align_mobile
  assign heading_mt = section.settings.heading_mt

  assign sub_heading = section.settings.sub_heading
  assign sub_heading_size = section.settings.sub_heading_size
  assign sub_heading_size_mobile = section.settings.sub_heading_size_mobile
  assign sub_heading_color = section.settings.sub_heading_color
  assign sub_heading_custom = section.settings.sub_heading_custom
  assign sub_heading_font = section.settings.sub_heading_font
  assign sub_heading_height = section.settings.sub_heading_height
  assign sub_heading_align = section.settings.sub_heading_align
  assign sub_heading_align_mobile = section.settings.sub_heading_align_mobile
  assign sub_heading_mt = section.settings.sub_heading_mt
  assign sub_heading_position = section.settings.sub_heading_position

  assign text = section.settings.text
  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_color = section.settings.text_color
  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_height = section.settings.text_height
  assign text_align = section.settings.text_align
  assign text_align_mobile = section.settings.text_align_mobile
  assign text_mt = section.settings.text_mt

  assign button = section.settings.button
  assign button_size = section.settings.button_size
  assign button_size_mobile = section.settings.button_size_mobile
  assign button_color = section.settings.button_color
  assign button_hover_color = section.settings.button_hover_color
  assign button_custom = section.settings.button_custom
  assign button_font = section.settings.button_font
  assign button_height = section.settings.button_height
  assign button_align = section.settings.button_align
  assign button_align_mobile = section.settings.button_align_mobile
  assign button_url = section.settings.button_url
  assign button_mt = section.settings.button_mt
  assign button_padding_vertical = section.settings.button_padding_vertical
  assign button_padding_horizontal = section.settings.button_padding_horizontal
  assign button_radius = section.settings.button_radius
  assign button_border_thickness = section.settings.button_border_thickness
  assign button_border_color = section.settings.button_border_color
  assign button_border_hover_color = section.settings.button_border_hover_color
  assign button_bg_color = section.settings.button_bg_color
  assign button_bg_hover_color = section.settings.button_bg_hover_color
-%}

{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  sub_heading_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .video-image-grid-{{ section.id }} {
    display: grid;
    grid-template-columns: repeat(1, 1fr);
    gap: {{ grid_gap_mobile }}px;
    align-items: center;
    padding: {{ grid_padding_vertical | times: 0.75 | round: 0 }}px {{ grid_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ grid_radius }}px;
    border: {{ grid_border_thickness }}px solid {{ grid_border_color }};
    background-color: {{ grid_bg_color }};
  }

  .video-image-media-{{ section.id }} {
    position: relative;
  }

  .video-image-media-{{ section.id }} img,
  .video-image-media-{{ section.id }} svg,
  .video-image-media-{{ section.id }} video,
  .video-image-media-{{ section.id }} iframe {
    display: block;
    object-fit: cover;
    width: 100%;
    height: 100%;
    box-shadow: none;
    border: 0px;
  }

  .video-image-media-{{ section.id }} svg {
    background-color: #DDDDDD;
  }

  .video-image-content-{{ section.id }} {
    padding: {{ content_padding_vertical | times: 0.75 | round: 0 }}px {{ content_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ content_radius }}px;
    border: {{ content_border_thickness }}px solid {{ content_border_color }};
    background-color: {{ content_bg_color }};
  }

  .video-image-heading-{{ section.id }} {
    margin-top: {{ heading_mt | times: 0.75 | round: 0 }}px;
    text-align: {{ heading_align_mobile }};
  }

  .video-image-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .video-image-subheading-{{ section.id }} {
    margin-top: {{ sub_heading_mt | times: 0.75 | round: 0 }}px;
    text-align: {{ sub_heading_align_mobile }};
  }

  .video-image-subheading-{{ section.id }} * {
    margin: 0;
    font-size: {{ sub_heading_size_mobile }}px;
    color: {{ sub_heading_color }};
    line-height: {{ sub_heading_height }}%;
    text-transform: unset;
  }

  .video-image-text-{{ section.id }} {
    margin-top: {{ text_mt | times: 0.75 | round: 0 }}px;
    text-align: {{ text_align_mobile }};
  }

  .video-image-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
  }

  .video-image-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: fit-content;
    margin: 0;
    margin-top: {{ button_mt | times: 0.75 | round: 0 }}px;
    font-size: {{ button_size_mobile }}px;
    font-weight: 700;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical | times: 0.75 | round: 0 }}px {{ button_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ button_radius }}px;
    border: {{ button_border_thickness }}px solid {{ button_border_color }};
    background-color: {{ button_bg_color }};
    transition: all 0.25s ease 0s;
  }

  .video-image-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    background-color: {{ button_bg_hover_color }};
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 576px) {
    .video-image-grid-{{ section.id }} {
      gap: {{ grid_gap_mobile | times: 1.5 | round: 0 }}px;
    }
  }

  @media(min-width: 768px) {
    .video-image-grid-{{ section.id }} {
      gap: {{ grid_gap_mobile | times: 2 }}px;
    }
  }

  @media(min-width: 850px) {
    .video-image-grid-{{ section.id }} {
      gap: {{ grid_gap_mobile | times: 2.3 | round: 0 }}px;
    }
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .video-image-grid-{{ section.id }} {
      grid-template-columns: repeat(2, 1fr);
      gap: {{ grid_gap }}px;
      padding: {{ grid_padding_vertical }}px {{ grid_padding_horizontal }}px;
    }

    .video-image-content-{{ section.id }} {
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
    }

    .video-image-heading-{{ section.id }} {
      margin-top: {{ heading_mt }}px;
      text-align: {{ heading_align }};
    }

    .video-image-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .video-image-subheading-{{ section.id }} {
      margin-top: {{ sub_heading_mt }}px;
      text-align: {{ sub_heading_align }};
    }

    .video-image-subheading-{{ section.id }} * {
      font-size: {{ sub_heading_size }}px;
    }

    .video-image-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ text_align }};
    }

    .video-image-text-{{ section.id }} *{
      font-size: {{text_size }}px;
    }

    .video-image-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .video-image-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if sub_heading_custom %}
  <style>
    .video-image-subheading-{{ section.id }} * {
      font-family: {{ sub_heading_font.family }}, {{ sub_heading_font.fallback_families }};
      font-weight: {{ sub_heading_font.weight }};
      font-style: {{ sub_heading_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .video-image-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .video-image-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if button_align_mobile == "center" %}
  <style>
   .video-image-button-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    } 
  </style>
{% elsif button_align_mobile == "right" %}
  <style>
   .video-image-button-{{ section.id }} {
      margin-left: auto;
    } 
  </style>
{% endif %}

{% if button_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .video-image-button-{{ section.id }} {
        margin-left: auto;
        margin-right: auto;
      } 
    }
  </style>
{% elsif button_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .video-image-button-{{ section.id }} {
       margin-right: 0px;
        margin-left: auto;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .video-image-button-{{ section.id }} {
        margin-left: 0px;
        margin-right: 0px;
      } 
    }
  </style>
{% endif %}

{% if media_position_mobile == "top" %}
  <style>
    .video-image-media-{{ section.id }} {
      order: 1;
    }
    .video-image-content-{{ section.id }} {
      order: 2;
    }
  </style>
{% else %}
  <style>
    .video-image-media-{{ section.id }} {
      order: 2;
    }
    .video-image-content-{{ section.id }} {
      order: 1;
    }
  </style>
{% endif %}

{% if media_position == "left" %}
  <style>
    @media(min-width: 1024px) {
      .video-image-media-{{ section.id }} {
        order: 1;
      }
      .video-image-content-{{ section.id }} {
        order: 2;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .video-image-media-{{ section.id }} {
        order: 2;
      }
      .video-image-content-{{ section.id }} {
        order: 1;
      } 
    }
  </style>
{% endif %}

{% unless section.blocks.size > 0 and section.blocks[0].type == 'media' or section.blocks[1].type == 'media' %}
  <style>
    @media(min-width: 1024px) {
      .video-image-grid-{{ section.id }} {
        grid-template-columns: repeat(1, 1fr);
      }
    }
  </style>
{% endunless %}

{% if grid_shadow %}
    <style>
      .video-image-grid-{{ section.id }} {
        box-shadow: {{ grid_shadow_color | hex_to_rgba: 0.02 }} 0px 4.5px 2px, {{ grid_shadow_color | hex_to_rgba: 0.02 }} 0px 8.1px 4.7px, {{ grid_shadow_color | hex_to_rgba: 0.03 }} 0px 10.7px 8.5px, {{ grid_shadow_color | hex_to_rgba: 0.03 }} 0px 12.8px 14.1px, {{ grid_shadow_color | hex_to_rgba: 0.05 }} 0px 14.8px 23.3px, {{ grid_shadow_color | hex_to_rgba: 0.05 }} 0px 17.9px 40.7px, {{ grid_shadow_color | hex_to_rgba: 0.06 }} 0px 29px 88px;
      }
    </style>
  {% endif %}

{% for block in section.blocks %}

  {% if block.type == "media" %}

    <style>
      .video-image-media-{{ section.id }} {
        border-radius: {{ block.settings.media_radius }}px;
        border: {{ block.settings.media_border_thickness }}px solid {{ block.settings.media_border_color }};
      }

      .video-image-media-{{ section.id }} iframe,
      .video-image-media-{{ section.id }} video,
      .video-image-media-{{ section.id }} img,
      .video-image-media-{{ section.id }} svg {
        border-radius: {{ block.settings.media_radius }}px;
      }
    </style>

    {% if block.settings.media_ratio == "portrait" %}
      <style>
        .video-image-media-{{ section.id }} {
         aspect-ratio: 9.6/12; 
        }
      </style>
    {% elsif block.settings.media_ratio == "landscape" %}
      <style>
        .video-image-media-{{ section.id }} {
         aspect-ratio: 12/8; 
        }
      </style>
    {% elsif block.settings.media_ratio == "square" %}
      <style>
        .video-image-media-{{ section.id }} {
         aspect-ratio: 12/12; 
        }
      </style>
    {% endif %}

    {% if block.settings.media_shadow %}
      <style>
        .video-image-media-{{ section.id }} {
         box-shadow: 0px 4px 5px rgba(0,0,0, 0.2);
        }
      </style>
    {% endif %}

  {% endif %}

  {% if block.type == "secondary_media" %}

    <style>
      .video-image-secondary-media-{{ section.id }} {
        position: absolute;
        width: 100%;
        right: {{ block.settings.media_horizontal_margin }}%;
        transform: translateX({{ block.settings.media_horizontal_margin }}%);
        {% if media_position_mobile == "top" %}
          bottom: {{ block.settings.media_margin_mobile }}%;
        {% else %}
          top: {{ block.settings.media_margin_mobile }}%;
        {% endif %}
        max-width: {{ block.settings.media_width_mobile }}%;
        border-radius: {{ block.settings.media_radius }}px;
        border: {{ block.settings.media_border_thickness }}px solid {{ block.settings.media_border_color }};
      }

      .video-image-secondary-media-{{ section.id }} iframe,
      .video-image-secondary-media-{{ section.id }} video,
      .video-image-secondary-media-{{ section.id }} img,
      .video-image-secondary-media-{{ section.id }} svg {
        border-radius: {{ block.settings.media_radius }}px;
      }

      @media(min-width: 1024px) {
        .video-image-secondary-media-{{ section.id }} {
          max-width: {{ block.settings.media_width }}%;
          transform: translateX(0px);
          right: unset;
          bottom: unset;
          {% if media_position == "left" %}
            right: {{ block.settings.media_margin }}%;
          {% else %}
            left: {{ block.settings.media_margin }}%;
          {% endif %}
          top: {{ block.settings.media_vertical_margin }}%;
          transform: translateY(-{{ block.settings.media_vertical_margin }}%);
        }
      }
    </style>

    {% if block.settings.media_ratio == "portrait" %}
      <style>
        .video-image-secondary-media-{{ section.id }} {
         aspect-ratio: 9.6/12; 
        }
      </style>
    {% elsif block.settings.media_ratio == "landscape" %}
      <style>
        .video-image-secondary-media-{{ section.id }} {
         aspect-ratio: 12/8; 
        }
      </style>
    {% elsif block.settings.media_ratio == "square" %}
      <style>
        .video-image-secondary-media-{{ section.id }} {
         aspect-ratio: 12/12; 
        }
      </style>
    {% endif %}

    {% if block.settings.media_shadow %}
      <style>
        .video-image-secondary-media-{{ section.id }} {
         box-shadow: 0px 4px 5px rgba(0,0,0, 0.2);
        }
      </style>
    {% endif %}

  {% endif %}

{% endfor %}

<div class="section-{{ section.id }} video-image-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="video-image-grid-{{ section.id }}">
        <div class="video-image-media-{{ section.id }}">
          {% if section.blocks.size > 1 %}
            <div class="video-image-secondary-media-{{ section.id }}">
              {% for block in section.blocks %}

                {% if block.type == "secondary_media" %}

                  {% if block.settings.media == "image" %}
                    {% if block.settings.image != blank %}
                      <img src="{{ block.settings.image | image_url }}" al="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                    {% else %}
                      {{ 'image' | placeholder_svg_tag }}
                    {% endif %}
                  {% else %}
                    {% assign video_id =  block.settings.video.id | default: block.settings.video_url.id %}
                    {%- if block.settings.video == null and block.settings.video_url != null -%}

                      {% if block.settings.loop %}
                        {% assign loop = '&loop=1&playlist=' | append: video_id %}
                      {% endif %}

                      {% if block.settings.autoplay %}
                        {% assign autoplay = '&mute=1&autoplay=1' %}
                      {% endif %}

                      {%- if block.settings.video_url.type == 'youtube' -%}
                        <iframe
                          src="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1{{ autoplay }}{{ loop }}"
                          class="js-youtube"
                          allow="autoplay; encrypted-media"
                          allowfullscreen
                          title="Fixed Video"
                        ></iframe>
                      {%- else -%}
                        <iframe
                          src="https://player.vimeo.com/video/{{ video_id }}{{ autoplay }}{{ loop }}"
                          class="js-vimeo"
                          allow="autoplay; encrypted-media"
                          allowfullscreen
                          title="Fixed Video"
                        ></iframe>
                    {% endif %}

                  {% else %}

                    {% assign poster_url = block.settings.poster | image_url %}

                    {{ block.settings.video | video_tag:
                        autoplay: block.settings.autoplay,
                        loop: block.settings.loop,
                        controls: block.settings.controls,
                        mute: true,
                        poster: poster_url,
                        id: "vid"
                    }}

                  {% endif %}

                {% endif %}

                {% endif %}

              {% endfor %}
            </div>
          {% endif %}
          {% for block in section.blocks %}
            {% if block.type == "media" %}

              {% if block.settings.media == "image" %}
                {% if block.settings.image != blank %}
                  <img src="{{ block.settings.image | image_url }}" al="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                {% else %}
                  {{ 'image' | placeholder_svg_tag }}
                {% endif %}
              {% else %}
                {% assign video_id =  block.settings.video.id | default: block.settings.video_url.id %}
                {%- if block.settings.video == null and block.settings.video_url != null -%}

                  {% if block.settings.loop %}
                    {% assign loop = '&loop=1&playlist=' | append: video_id %}
                  {% endif %}

                  {% if block.settings.autoplay %}
                    {% assign autoplay = '&mute=1&autoplay=1' %}
                  {% endif %}

                  {%- if block.settings.video_url.type == 'youtube' -%}
                    <iframe
                      data-src="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1{{ autoplay }}{{ loop }}"
                      class="js-youtube lazy"
                      allow="autoplay; encrypted-media"
                      allowfullscreen
                      title="Fixed Video"
                    ></iframe>
                  {%- else -%}
                    <iframe
                      data-src="https://player.vimeo.com/video/{{ video_id }}{{ autoplay }}{{ loop }}"
                      class="js-vimeo lazy"
                      allow="autoplay; encrypted-media"
                      allowfullscreen
                      title="Fixed Video"
                    ></iframe>
                {% endif %}

              {% else %}

                  <video
                    data-src="{{ block.settings.video.sources[1].url }}"
                    {% if block.settings.autoplay %}
                    autoplay
                    muted
                    {% endif %}
                    playsinline
                    preload="none"
                    {% if block.settings.loop %}
                    loop
                    {% endif %}
                    {% if block.settings.controls %}
                    controls
                    {% endif %}
                    {% if block.settings.poster != blank %}
                    poster="{{ block.settings.poster | image_url }}"
                    {% endif %}
                    class="lazy"
                  ></video>

              {% endif %}

            {% endif %}

            {% endif %}
          {% endfor %}
        </div>
        <div class="video-image-content-{{ section.id }}">
          {% if sub_heading != blank and sub_heading_position == "above" %}
            <div class="video-image-subheading-{{ section.id }}">{{ sub_heading }}</div>
          {% endif %}

          {% if heading != blank %}
            <div class="video-image-heading-{{ section.id }}">{{ heading }}</div>
          {% endif %}

          {% if sub_heading != blank and sub_heading_position == "below" %}
            <div class="video-image-subheading-{{ section.id }}">{{ sub_heading }}</div>
          {% endif %}

          {% if text != blank %}
            <div class="video-image-text-{{ section.id }}">{{ text }}</div>
          {% endif %}

