  endif

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_height = section.settings.heading_height
  assign heading_color = section.settings.heading_color   

  assign pagination_padding_horizontal = section.settings.pagination_padding_horizontal
  assign pagination_padding_horizontal_mobile = section.settings.pagination_padding_horizontal_mobile
  assign pagination_padding_vertical = section.settings.pagination_padding_vertical
  assign pagination_padding_vertical_mobile = section.settings.pagination_padding_vertical_mobile
  assign pagination_bg_color = section.settings.pagination_bg_color
  assign pagination_radius = section.settings.pagination_radius
  assign pagination_custom = section.settings.pagination_custom
  assign pagination_font = section.settings.pagination_font
  assign pagination_size = section.settings.pagination_size
  assign pagination_size_mobile = section.settings.pagination_size_mobile
  assign pagination_height = section.settings.pagination_height
  assign pagination_color = section.settings.pagination_color  
  assign pagination_mt = section.settings.pagination_mt
  assign pagination_mt_mobile = section.settings.pagination_mt_mobile

  assign sub_heading_custom = section.settings.sub_heading_custom
  assign sub_heading_font = section.settings.sub_heading_font
  assign sub_heading_size = section.settings.sub_heading_size
  assign sub_heading_size_mobile = section.settings.sub_heading_size_mobile
  assign sub_heading_height = section.settings.sub_heading_height
  assign sub_heading_color = section.settings.sub_heading_color  
  assign sub_heading_mt = section.settings.sub_heading_mt
  assign sub_heading_mt_mobile = section.settings.sub_heading_mt_mobile 

  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_height = section.settings.text_height
  assign text_color = section.settings.text_color   
  assign text_mt = section.settings.text_mt
  assign text_mt_mobile = section.settings.text_mt_mobile

  assign arrow_size = section.settings.arrow_size
  assign arrow_size_mobile = section.settings.arrow_size_mobile
  assign arrow_icon_size = section.settings.arrow_icon_size
  assign arrow_icon_size_mobile = section.settings.arrow_icon_size_mobile
  assign arrow_color = section.settings.arrow_color
  assign arrow_hover_color = section.settings.arrow_hover_color
  assign arrow_bg_color = section.settings.arrow_bg_color
  assign arrow_bg_hover_color = section.settings.arrow_bg_hover_color
  assign arrow_radius = section.settings.arrow_radius
  assign arrow_border_thickness = section.settings.arrow_border_thickness
  assign arrow_border_color = section.settings.arrow_border_color
  assign arrow_border_hover_color = section.settings.arrow_border_hover_color
  assign arrow_hover_effect = section.settings.arrow_hover_effect
  assign arrow_mt = section.settings.arrow_mt
  assign arrow_mt_mobile = section.settings.arrow_mt_mobile
-%}

{%- style -%}
  {{ heading_font | font_face: font_display: 'swap' }}
  {{ sub_heading_font | font_face: font_display: 'swap' }}
  {{ text_font | font_face: font_display: 'swap' }}
  {{ pagination_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .video-image-item-{{ section.id }} {
    display: none;
    grid-template-columns: 1fr;
    gap: {{ layout_gap_mobile }}px;
  }

  .video-image-item-{{ section.id }}.active {
    display: grid;
  }

  .video-image-content-{{ section.id }} {
    grid-area: content;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    padding: {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px;
  }

  .video-image-heading-{{ section.id }} {
    text-align: {{ content_align_mobile }};
  }

  .video-image-heading-{{ section.id }} * {
    margin: 0;   
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    word-break: break-word;
  }

  .video-image-content-wrapper-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: {{ content_horizontal_align_mobile }};
  }

  .video-image-pagination-{{ section.id }} {
    margin: 0;  
    max-width: fit-content;
    margin-top: {{ pagination_mt_mobile }}px;
    padding: {{ pagination_padding_vertical_mobile }}px {{ pagination_padding_horizontal_mobile }}px;
    border-radius: {{ pagination_radius }}px; 
    font-size: {{ pagination_size_mobile }}px;
    color: {{ pagination_color }};
    background-color: {{ pagination_bg_color }};
    line-height: {{ pagination_height }}%;
    text-transform: unset;
    word-break: break-word;
  }

  .video-image-subheading-{{ section.id }} {
    margin-top: {{ sub_heading_mt_mobile }}px;
    text-align: {{ content_align_mobile }};
  }

  .video-image-subheading-{{ section.id }} * {
    margin: 0;      
    font-size: {{ sub_heading_size_mobile }}px;
    color: {{ sub_heading_color }};
    line-height: {{ sub_heading_height }}%;  
    text-transform: unset;
    word-break: break-word;
  }

  .video-image-text-{{ section.id }} {
    text-align: {{ content_align_mobile }};
    margin-top: {{ text_mt_mobile }}px;
  }

  .video-image-text-{{ section.id }} * {
    margin: 0;   
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    word-break: break-word;
  }

  .video-image-small-media-{{ section.id }} {
    width: 50%;
    margin-top: {{ sm_media_mt_mobile }}px;
    border: {{ sm_media_border_thickness }}px solid {{ sm_media_border_color }};
    border-radius: {{ sm_media_radius }}px;
    overflow: hidden;
  }

  .video-image-small-media-{{ section.id }} img,
  .video-image-small-media-{{ section.id }} svg,
  .video-image-small-media-{{ section.id }} video,
  .video-image-small-media-{{ section.id }} iframe {
    border: 0;
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .video-image-media-{{ section.id }} {
    grid-area: media;
    border: {{ media_border_thickness }}px solid {{ media_border_color }};
    border-radius: {{ media_radius }}px;
    overflow: hidden;
    height: auto;
    align-self:center;
  }

  .video-image-media-{{ section.id }} img,
  .video-image-media-{{ section.id }} svg,
  .video-image-media-{{ section.id }} video,
  .video-image-media-{{ section.id }} iframe {
    display: block;
    border: 0px;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .video-image-media-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .video-image-buttons-{{ section.id }} {
    display: flex;
    margin-top: {{ arrow_mt_mobile }}px;
    align-items: center;
    gap: 10px;
    justify-content: {{ content_horizontal_align_mobile }};
  }

  .video-image-btn-prev-{{ section.id }},
  .video-image-btn-next-{{ section.id }} {
    display: flex;
    width: {{ arrow_size_mobile }}px;
    height: {{ arrow_size_mobile }}px;
    border: 0px;
    border: {{ arrow_border_thickness }}px solid {{ arrow_border_color }};
    border-radius: {{ arrow_radius }}px;
    background: transparent;
    background: {{ arrow_bg_color }};
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease 0s;
    cursor: pointer;
    -webkit-appearance: none;
    /* box-shadow: 0px 0px 10px 0px rgba(0,0,0,.15); */
  }

  .video-image-btn-prev-{{ section.id }} .animate-icon,
  .video-image-btn-next-{{ section.id }} .animate-icon {
    pointer-events: none;
    place-items: center;
    display: grid;
  }

  .video-image-btn-prev-{{ section.id }} .animate-icon:before,
  .video-image-btn-next-{{ section.id }} .animate-icon:before {
    content: "";
    grid-area: 1 / -1;
    transition: all .2s ease;
    width: {{ arrow_icon_size_mobile }}px;
    height: {{ arrow_icon_size_mobile }}px;
    transform: rotate(calc(1 * 45deg));
    box-sizing: border-box;
    border-style: solid;
    border: 0px;
    border-right: 2px solid currentColor;
    border-top: 2px solid currentColor;
    position: relative;
    left: -1px;
  }

  .video-image-btn-prev-{{ section.id }} .animate-icon:before {
    left: unset;
    right: -1px;
    border-right: 0px;
    border-top: 0px;
    border-bottom: 2px solid currentColor;
    border-left: 2px solid currentColor;
  }

  .video-image-btn-prev-{{ section.id }} .animate-icon:after,
  .video-image-btn-next-{{ section.id }} .animate-icon:after {
    content: "";
    grid-area: 1 / -1;
    transition: all .2s ease;
    width: {{ arrow_icon_size_mobile | minus: 0.3 }}px;
    height: 1.6px;
    transform-origin: center;
    opacity: 0;
    background: currentColor;
    transform: scaleX(.5);
  }

  .video-image-btn-prev-{{ section.id }} span,
  .video-image-btn-next-{{ section.id }} span {
    color: {{ arrow_color }};
  }

  .video-image-btn-prev-{{ section.id }}.swiper-button-disabled,
  .video-image-btn-next-{{ section.id }}.swiper-button-disabled {
    opacity: 0.5;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .video-image-item-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
      gap: {{ layout_gap }}px;
    }

    .video-image-content-{{ section.id }} {
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
    }

    .video-image-heading-{{ section.id }} {
      text-align: {{ content_align }};
    }

    .video-image-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .video-image-content-wrapper-{{ section.id }} {
      align-items: {{ content_horizontal_align }};
    }

    .video-image-pagination-{{ section.id }} {
      margin-top: {{ pagination_mt }}px;
      padding: {{ pagination_padding_vertical }}px {{ pagination_padding_horizontal }}px;
      font-size: {{ pagination_size }}px;
    }

    .video-image-subheading-{{ section.id }} {
      margin-top: {{ sub_heading_mt }}px;
      text-align: {{ content_align }};
    }

    .video-image-subheading-{{ section.id }} * {
      font-size: {{ sub_heading_size }}px;
    }

    .video-image-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ content_align }};
    }

    .video-image-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .video-image-buttons-{{ section.id }} {
      margin-top: {{ arrow_mt }}px;
      justify-content: {{ content_horizontal_align }};
    }

    .video-image-btn-prev-{{ section.id }},
    .video-image-btn-next-{{ section.id }} {
      width: {{ arrow_size }}px;
      height: {{ arrow_size }}px;
      transition: all 0.25s ease 0s;
    }

    .video-image-btn-prev-{{ section.id }} .animate-icon:before,
    .video-image-btn-next-{{ section.id }} .animate-icon:before {
      width: {{ arrow_icon_size }}px;
      height: {{ arrow_icon_size }}px;
    }

    .video-image-btn-prev-{{ section.id }} .animate-icon:after,
    .video-image-btn-next-{{ section.id }} .animate-icon:after {
      width: {{ arrow_icon_size | minus: 0.3 }}px;
    }

    .video-image-small-media-{{ section.id }} {
      margin-top: {{ sm_media_mt }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .video-image-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if pagination_custom %}
  <style>
    .video-image-pagination-{{ section.id }} {
      font-family: {{ pagination_font.family }}, {{ pagination_font.fallback_families }};
      font-weight: {{ pagination_font.weight }};
      font-style: {{ pagination_font.style }};
    }
  </style>
{% endif %}

{% if sub_heading_custom %}
  <style>
    .video-image-subheading-{{ section.id }} * {
      font-family: {{ sub_heading_font.family }}, {{ sub_heading_font.fallback_families }};
      font-weight: {{ sub_heading_font.weight }};
      font-style: {{ sub_heading_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .video-image-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}


{% if media_ratio_mobile == "portrait" %}
  <style>
    .video-image-media-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif media_ratio_mobile == "landscape" %}
  <style>
  .video-image-media-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif media_ratio_mobile == "square" %}
  <style>
    .video-image-media-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .video-image-media-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if media_ratio == "portrait" %}
  <style>
    @media (min-width: 1024px) {
      .video-image-media-{{ section.id }} {
        aspect-ratio: 9.6/13;
      }
    }
  </style>
{% elsif media_ratio == "landscape" %}
  <style>
    @media (min-width: 1024px) {
      .video-image-media-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }
  </style>
{% elsif media_ratio == "square" %}
  <style>
    @media (min-width: 1024px) {
      .video-image-media-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }
  </style>
{% else %}
  <style>
    @media (min-width: 1024px) {
      .video-image-media-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}


{% if sm_media_ratio_mobile == "portrait" %}
  <style>
    .video-image-small-media-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif sm_media_ratio_mobile == "landscape" %}
  <style>
  .video-image-small-media-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif sm_media_ratio_mobile == "square" %}
  <style>
    .video-image-small-media-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .video-image-small-media-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if sm_media_ratio == "portrait" %}
  <style>
    @media (min-width: 1024px) {
      .video-image-small-media-{{ section.id }} {
        aspect-ratio: 9.6/13;
      }
    }
  </style>
{% elsif sm_media_ratio == "landscape" %}
  <style>
    @media (min-width: 1024px) {
      .video-image-small-media-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }
  </style>
{% elsif sm_media_ratio == "square" %}
  <style>
    @media (min-width: 1024px) {
      .video-image-small-media-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }
  </style>
{% else %}
  <style>
    @media (min-width: 1024px) {
      .video-image-small-media-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}

{% if layout_mobile == 'content_media'%}
  <style>
    .video-image-item-{{ section.id }} {
      grid-template-areas: 
        "content"
        "media";     
    }
  </style>
{% elsif layout_mobile == 'media_content' %}
  <style>
    .video-image-item-{{ section.id }} {
      grid-template-areas: 
        "media"
        "content";     
    }
  </style>
{% endif %}

{% if layout == 'content_media'%}
  <style>
    @media(min-width: 1024px) {
      .video-image-item-{{ section.id }} {
        grid-template-areas: "content media";    
      }
    }    
  </style>
{% elsif layout == 'media_content' %}
  <style>
    @media(min-width: 1024px) {
      .video-image-item-{{ section.id }} {
        grid-template-areas: "media content";   
      }
    }   
  </style>
{% endif %}

{% if arrow_hover_effect == "color" %}
  <style>
    .video-image-btn-prev-{{ section.id }}:hover,
    .video-image-btn-next-{{ section.id }}:hover {
      transition: all 0.3s ease 0s;
      border: {{ arrow_border_thickness }}px solid {{ arrow_border_hover_color }};
      background: {{ arrow_bg_hover_color }};
    }

    .video-image-btn-prev-{{ section.id }}:hover .animate-icon,
    .video-image-btn-next-{{ section.id }}:hover .animate-icon {
      color: {{ arrow_hover_color }};
    }
  </style>
{% elsif arrow_hover_effect == "scale" %}
  <style>
    .video-image-btn-prev-{{ section.id }}:hover,
    .video-image-btn-next-{{ section.id }}:hover {
      transform: scale(0.9);
    }

    @media(min-width: 1024px) {
      .video-image-btn-prev-{{ section.id }}:hover,
      .video-image-btn-next-{{ section.id }}:hover {
        transform: scale(0.9);
      }
    }
  </style>
{% elsif arrow_hover_effect == "arrow" %}
  <style>
    .video-image-btn-next-{{ section.id }}:hover .animate-icon:before {
      transform: translate(calc(1 * 0.125rem)) rotate(calc(1 * 45deg));
    }

    .video-image-btn-prev-{{ section.id }}:hover .animate-icon:before {
      transform: translate(calc(-1 * 0.125rem)) rotate(calc(1 * 45deg));
    }

    .video-image-btn-prev-{{ section.id }}:hover .animate-icon:after,
    .video-image-btn-next-{{ section.id }}:hover .animate-icon:after {
      opacity: 1;
      transform: scaleX(1);
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} video-image-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">     
      {% for block in section.blocks %}
        <div class="video-image-item-{{ section.id }} {% if forloop.first %}active{% endif %}" data-index="{{ forloop.index }}">
          <div class="video-image-content-{{ section.id }}">
            {% if block.settings.heading != blank %}
              <div class="video-image-heading-{{ section.id }}">{{ block.settings.heading }}</div>
            {% endif %}

