<script src="https://cdn.jsdelivr.net/npm/vanilla-lazyload@19.1.3/dist/lazyload.min.js"></script>

<script>
  function initVideoImageWithText5() {
    let lazyLoadInstance = new LazyLoad();

    window.addEventListener(
      "LazyLoad::Initialized",
      function (event) {
        window.lazyLoadInstance = event.detail.instance;
      },
      false
    );

    function switchSlide(currentIndex, direction) {
      const slides = document.querySelectorAll(`.video-image-item-{{ section.id }}`);
      const totalSlides = slides.length;

      let newIndex;
      if (direction === 'next') {
        newIndex = (currentIndex % totalSlides) + 1;
      } else if (direction === 'prev') {
        newIndex = (currentIndex - 2 + totalSlides) % totalSlides + 1;
      }

      slides.forEach((slide, index) => {
        if (index + 1 === newIndex) {
          slide.classList.add('active');
        } else {
          slide.classList.remove('active');
        }
      });
    }

    document.querySelectorAll(`.video-image-btn-next-{{ section.id }}`).forEach(button => {
      button.addEventListener('click', () => {
        const currentIndex = parseInt(button.getAttribute('data-index'), 10);
        switchSlide(currentIndex, 'next');
      });
    });

    document.querySelectorAll(`.video-image-btn-prev-{{ section.id }}`).forEach(button => {
      button.addEventListener('click', () => {
        const currentIndex = parseInt(button.getAttribute('data-index'), 10);
        switchSlide(currentIndex, 'prev');
      });
    });
  }

  document.addEventListener('DOMContentLoaded', initVideoImageWithText5);

  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', initVideoImageWithText5);
    document.addEventListener('shopify:section:load', initVideoImageWithText5);
  }
</script>
