liquid_code:
{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign layout = section.settings.layout
  assign layout_mobile = section.settings.layout_mobile
  assign layout_gap = section.settings.layout_gap
  assign layout_gap_mobile = section.settings.layout_gap_mobile

  assign media_view_mobile = section.settings.media_view_mobile

  assign content_padding_horizontal = section.settings.content_padding_horizontal
  assign content_padding_horizontal_mobile = section.settings.content_padding_horizontal_mobile
  assign content_padding_vertical = section.settings.content_padding_vertical
  assign content_padding_vertical_mobile = section.settings.content_padding_vertical_mobile
  assign content_align = section.settings.content_align
  assign content_align_mobile = section.settings.content_align_mobile
  assign content_vertical_align = section.settings.content_vertical_align

  assign content_horizontal_align = ""
  if content_align == "center"
    assign content_horizontal_align = "center"
  elsif content_align == "right"
    assign content_horizontal_align = "end"
  else
    assign content_horizontal_align = "start"
  endif

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign play_icon_size = section.settings.play_icon_size
  assign play_icon_size_mobile = section.settings.play_icon_size_mobile
  assign play_icon_color = section.settings.play_icon_color
  assign play_icon_color_active = section.settings.play_icon_color_active
  assign play_icon_bg_color = section.settings.play_icon_bg_color
  assign play_icon_bg_color_active = section.settings.play_icon_bg_color_active
  assign play_icon_radius = section.settings.play_icon_radius

  assign mute_icon_size = section.settings.mute_icon_size
  assign mute_icon_size_mobile = section.settings.mute_icon_size_mobile
  assign mute_icon_color = section.settings.mute_icon_color
  assign mute_icon_color_active = section.settings.mute_icon_color_active
  assign mute_icon_bg_color = section.settings.mute_icon_bg_color
  assign mute_icon_bg_color_active = section.settings.mute_icon_bg_color_active
  assign mute_icon_radius = section.settings.mute_icon_radius

  assign loading_attribute = 'lazy'
  unless lazy
    assign loading_attribute = 'eiger'
  endunless
-%}

{%- style -%}
  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .image-text-body-{{ section.id }} {
    display: grid;
    gap: {{ layout_gap_mobile }}px;
  }

  .image-text-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    justify-content: {{ content_vertical_align }};
    align-items: {{ content_horizontal_align_mobile }};
    padding: {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px;
    grid-area: content;
  }

  .image-text-columns-{{ section.id }} {
    grid-area: media;
    gap: 16px;
    display: grid;
    align-items: center;
    grid-template-columns: repeat({{ media_view_mobile }}, 1fr);
  }

  .image-text-column-{{ section.id }} {
    display: flex;
    flex-direction: column;
    gap: 16px;
  }

  .image-text-media-play-{{ section.id }} {
    position: absolute;
    top: 10px;
    right: 10px;
    padding: {{ play_icon_size_mobile | times: 0.75 }}px;
    cursor: pointer;
    border-radius: {{ play_icon_radius }}px;
    background-color: {{ play_icon_bg_color }};
  }

  .image-text-media-play-{{ section.id }}.active {
    background-color: {{ play_icon_bg_color_active }};
  }

  .image-text-media-play-{{ section.id }} svg {
    display: block;
    background-color: transparent !important;
    width: {{ play_icon_size_mobile }}px !important;
    height: {{ play_icon_size_mobile }}px !important;
    object-fit: cover;
  }

  .image-text-media-play-{{ section.id }} svg:nth-child(1) path {
    fill: {{ play_icon_color }};
    stroke: {{ play_icon_color }};
  }

  .image-text-media-play-{{ section.id }} svg:nth-child(2) path {
    fill: {{ play_icon_color_active }};
    stroke: {{ play_icon_color_active }};
  }

  .image-text-media-mute-{{ section.id }} {
    position: absolute;
    top: 10px;
    left: 10px;
    padding: {{ mute_icon_size_mobile | times: 0.75 }}px;
    cursor: pointer;
    border-radius: {{ mute_icon_radius }}px;
    background-color: {{ mute_icon_bg_color }};
  }

  .image-text-media-mute-{{ section.id }} svg:nth-child(1) path {
    fill: {{ mute_icon_color }};
  }

  .image-text-media-mute-{{ section.id }} svg:nth-child(2) path {
    fill: {{ mute_icon_color_active }};
  }

  .image-text-media-mute-{{ section.id }} svg:nth-child(1) {
    display: none;
  }

  .image-text-media-mute-{{ section.id }}.active svg:nth-child(1) {
    display: block;
  }

  .image-text-media-mute-{{ section.id }}.active svg:nth-child(2) {
    display: none;
  }

  .image-text-media-play-{{ section.id }} svg:nth-child(1) {
    display: none;
  }

  .image-text-media-play-{{ section.id }}.active svg:nth-child(1) {
    display: block;
  }

  .image-text-media-play-{{ section.id }}.active svg:nth-child(2) {
    display: none;
  }

  .image-text-media-mute-{{ section.id }}.active {
    background-color: {{ mute_icon_bg_color_active }};
  }

  .image-text-media-mute-{{ section.id }} svg {
    display: block;
    background-color: transparent !important;
    width: {{ mute_icon_size_mobile }}px !important;
    height: {{ mute_icon_size_mobile }}px !important;
    object-fit: cover;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .image-text-columns-{{ section.id }} {
      grid-template-columns: repeat(2, 1fr);
    }

    .image-text-body-{{ section.id }} {
      gap: {{ layout_gap }}px;
    }

    .image-text-content-{{ section.id }} {
      align-items: {{ content_horizontal_align }};
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
    }

    .image-text-media-mute-{{ section.id }} {
      padding: {{ mute_icon_size | times: 0.75 }}px;
    }

    .image-text-media-mute-{{ section.id }} svg {
      width: {{ mute_icon_size }}px !important;
      height: {{ mute_icon_size }}px !important;
    }

    .image-text-media-play-{{ section.id }} {
      padding: {{ play_icon_size | times: 0.75 }}px;
    }

    .image-text-media-play-{{ section.id }} svg {
      width: {{ play_icon_size }}px !important;
      height: {{ play_icon_size }}px !important;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if layout_mobile == "media_content" %}
  <style>
    .image-text-body-{{ section.id }} {
      grid-template-columns: 1fr;
      grid-template-areas: 
        "media" 
        "content"; 
    }
  </style>
{% else %} 
  {% if layout_mobile == "content_media" %}
    <style>
      .image-text-body-{{ section.id }} {
        grid-template-columns: 1fr;
        grid-template-areas: 
          "content" 
          "media";    
      }
    </style>
  {% endif %}  
{% endif %}

{% if layout == "media_content" %}
  <style>
    @media (min-width: 1024px) {
      .image-text-body-{{ section.id }} {
        grid-template-columns: 1fr 1fr;
        grid-template-areas: "media content";
      }
    }
  </style>
{% else %}  
  {% if layout == "content_media" %}
    <style>
      @media (min-width: 1024px) {
        .image-text-body-{{ section.id }} {
          grid-template-columns: 1fr 1fr;
          grid-template-areas: "content media";
        }
      }
    </style>
  {% endif %}  
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'heading' %}  
      <style>
        {{  block.settings.heading_font | font_face: font_display: 'swap' }}

        .image-text-heading-{{ block.id }} {
          margin-top: {{ block.settings.heading_mt_mobile }}px;
          text-align: {{ content_align_mobile }};
          position: relative;
          z-index: 2;
        }

        .image-text-heading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
          word-break: break-word;
          font-weight: 700;
        }

        @media(min-width: 1024px) {

          .image-text-heading-{{ block.id }} {
            margin-top: {{ block.settings.heading_mt }}px;
            text-align: {{ content_align }};
          }

          .image-text-heading-{{ block.id }} * {
            font-size: {{ block.settings.heading_size }}px;
          }
        }          
      </style>

      {% if block.settings.heading_custom %}
        <style>
          .image-text-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}



    {% when 'text' %}
      <style>        
        {{  block.settings.text_font | font_face: font_display: 'swap' }}

        .image-text-text-{{ block.id }} {
          margin-top: {{ block.settings.text_mt_mobile }}px;
          text-align: {{ content_align_mobile }};
        }

        .image-text-text-{{ block.id }} * {
          margin: 0;          
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
          font-weight: 400;
        }

        @media(min-width: 1024px) {

          .image-text-text-{{ block.id }} {
            margin-top: {{ block.settings.text_mt }}px;
            text-align: {{  content_align }};
          }         

          .image-text-text-{{ block.id }} * {       
            font-size: {{ block.settings.text_size }}px;
          }
        }
      </style>

      {% if block.settings.text_custom %}
        <style>
          .image-text-text-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>          
      {% endif %}  
    {% when 'button' %}
      <style>
        {{  block.settings.button_font | font_face: font_display: 'swap' }}

        .image-text-button-{{ block.id }} {
          display: flex;
          align-items: center;
          justify-content: center;
          max-width: fit-content;
          gap: 10px;
          width: 100%;
          margin: 0;
          margin-top: {{ block.settings.button_mt_mobile }}px;
          font-size: {{ block.settings.button_size_mobile }}px;
          color: {{ block.settings.button_color }};
          line-height: {{ block.settings.button_height }}%;
          text-align: center;
          text-transform: unset;
          text-decoration: none;
          padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
          border-radius: {{ block.settings.button_radius }}px;
          transition: all 0.3s ease 0s;
          background-color: transparent;
          border: 0px;
          cursor: pointer;
          font-weight: 700;
        }

        .image-text-button-inner-{{ block.id }} {
          margin: 0px;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 10px;
          text-decoration: none;
        }

        .image-text-button-{{ block.id }}:hover {
          color: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        .image-text-button-{{ block.id }} svg {
          width: 14px;
          height: 14px;
        }

        .image-text-button-{{ block.id }} svg path {
          fill: {{ block.settings.button_color }};
          transition: all 0.25s ease 0s;
        }

        .image-text-button-{{ block.id }}:hover svg path {
          fill: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        @media (min-width: 1024px) {
          .image-text-button-{{ block.id }} {
            margin-top: {{ block.settings.button_mt }}px;
            padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
            font-size: {{ block.settings.button_size }}px;
          }
        }
      </style>

      {% if block.settings.button_custom %}
        <style>
          .image-text-button-{{ block.id }} {
            font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
            font-weight: {{ block.settings.button_font.weight }};
            font-style: {{ block.settings.button_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.button_style == "non_outline" or block.settings.button_style == "non_outline_arrow" %}
        <style>
          .image-text-button-{{ block.id }} {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_color }};
          }

          .image-text-button-{{ block.id }}:hover {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "outline" or block.settings.button_style == "outline_arrow" %}
        <style>          
          .image-text-button-{{ block.id }} {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_color }};
            border: {{  block.settings.button_border_thickness }}px solid {{  block.settings.button_border_color }};
          }

          .image-text-button-{{ block.id }}:hover {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_hover_color }};
            border: {{  block.settings.button_border_thickness }}px solid {{ block.settings.button_border_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "link" %}
        <style>
          .image-text-button-{{ block.id }} {
            position: relative;
            padding: 0px !important;
            -webkit-box-shadow: none !important;
            -moz-box-shadow: none !important;
            box-shadow: none !important;            
          }

          .image-text-button-{{ block.id }}:after {
            content: "";
            position: absolute;
            bottom: -8px;
            left: 0;
            width: 100%;
            height: {{ block.settings.button_border_thickness }}px;
            background-color: {{ block.settings.button_border_color }};
            transition: all 0.25s ease 0s;
          }

          .image-text-button-{{ block.id }}:hover:after {
            background-color: {{ block.settings.button_border_hover_color }};
            transition: all 0.25s ease 0s;
          }
        </style>
      {% endif %}  

    {% when 'testimonial' %}
      {%- assign testimonial_horizontal_align = "start" -%}
      {% if block.settings.testimonial_align == "center" %}
        {%- assign testimonial_horizontal_align = "center" -%}
      {% elsif block.settings.testimonial_align == "right" %}
        {%- assign testimonial_horizontal_align = "end" -%}
      {% endif %}

      {%- assign testimonial_horizontal_align_mobile = "start" -%}
      {% if block.settings.testimonial_align_mobile == "center" %}
        {%- assign testimonial_horizontal_align_mobile = "center" -%}
      {% elsif block.settings.testimonial_align_mobile == "right" %}
        {%- assign testimonial_horizontal_align_mobile = "end" -%}
      {% endif %}

      <style>
        {{ block.settings.testimonial_text_font | font_face: font_display: 'swap' }}
        {{ block.settings.author_font | font_face: font_display: 'swap' }}
        {{ block.settings.caption_font | font_face: font_display: 'swap' }}

        .image-text-testimonial-{{ block.id }} {
          display: flex;
          flex-direction: column;
          align-items: {{ testimonial_horizontal_align_mobile }};
          padding: {{ block.settings.testimonial_padding_vertical_mobile }}px {{ block.settings.testimonial_padding_horizontal_mobile }}px;
          border: {{ block.settings.testimonial_border_thickness }}px solid {{ block.settings.testimonial_border_color }};
          border-radius: {{ block.settings.testimonial_radius }}px;
          background-color: {{ block.settings.testimonial_bg_color }};
        }

        .image-text-testimonial-text-{{ block.id }} {
          text-align: {{ block.settings.testimonial_align_mobile }};
        }

        .image-text-testimonial-text-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.testimonial_text_size_mobile }}px;
          color: {{ block.settings.testimonial_text_color }};
          line-height: {{ block.settings.testimonial_text_height }}%;
          text-transform: unset;
          text-decoration: none;
        }

        .image-text-testimonial-image-{{ block.id }} {
          max-width: {{ block.settings.author_image_width_mobile }}px;
          width: 100%;
          border: {{ block.settings.author_image_border_thickness }}px solid {{ block.settings.author_image_border_color }};
          border-radius: {{ block.settings.author_image_radius }}px;
          overflow: hidden;
          margin-top: {{ block.settings.author_image_mt_mobile }}px;
        }

        .image-text-testimonial-image-{{ block.id }} img,
        .image-text-testimonial-image-{{ block.id }} svg {
          display: block;
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        .image-text-testimonial-image-{{ block.id }} svg {
          background-color: #AFAFAF;
        }

        .image-text-testimonial-author-{{ block.id }} {
          margin: 0;
          text-align: {{ block.settings.testimonial_align_mobile }};
          margin-top: {{ block.settings.author_mt_mobile }}px;
          font-size: {{ block.settings.author_size_mobile }}px;
          color: {{ block.settings.author_color }};
          line-height: {{ block.settings.author_height }}%;
          font-weight: 700;
          text-transform: unset;
          text-decoration: none;
        }

        .image-text-testimonial-caption-{{ block.id }} {
          margin: 0;
          text-align: {{ block.settings.testimonial_align_mobile }};
          margin-top: {{ block.settings.caption_mt_mobile }}px;
          font-size: {{ block.settings.caption_size_mobile }}px;
          color: {{ block.settings.caption_color }};
          line-height: {{ block.settings.caption_height }}%;
          text-transform: unset;
          text-decoration: none;
        }

        @media(min-width: 1024px) {
          .image-text-testimonial-{{ block.id }} {
            align-items: {{ testimonial_horizontal_align }};
            padding: {{ block.settings.testimonial_padding_vertical }}px {{ block.settings.testimonial_padding_horizontal }}px;     
          }

          .image-text-testimonial-text-{{ block.id }} {
            text-align: {{ block.settings.testimonial_align }};
          }

          .image-text-testimonial-text-{{ block.id }} * {
            font-size: {{ block.settings.testimonial_text_size }}px;
          }

          .image-text-testimonial-image-{{ block.id }} {
            max-width: {{ block.settings.author_image_width }}px;
            margin-top: {{ block.settings.author_image_mt }}px;
          }

          .image-text-testimonial-author-{{ block.id }} {
            margin-top: {{ block.settings.author_mt }}px;
            font-size: {{ block.settings.author_size }}px;
            text-align: {{ block.settings.testimonial_align }};
          }

          .image-text-testimonial-caption-{{ block.id }} {
            margin-top: {{ block.settings.caption_mt }}px;
            font-size: {{ block.settings.caption_size }}px;
            text-align: {{ block.settings.testimonial_align }};
          }
        }
      </style>

      {% if block.settings.testimonial_text_custom %}
        <style>
          .image-text-testimonial-text-{{ block.id }} * {
            font-family: {{ block.settings.testimonial_text_font.family }}, {{ block.settings.testimonial_text_font.fallback_families }};
            font-weight: {{ block.settings.testimonial_text_font.weight }};
            font-style: {{ block.settings.testimonial_text_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.author_custom %}
        <style>
          .image-text-testimonial-author-{{ block.id }} {
            font-family: {{ block.settings.author_font.family }}, {{ block.settings.author_font.fallback_families }};
            font-weight: {{ block.settings.author_font.weight }};
            font-style: {{ block.settings.author_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.caption_custom %}
        <style>
          .image-text-testimonial-caption-{{ block.id }}{
            font-family: {{ block.settings.caption_font.family }}, {{ block.settings.caption_font.fallback_families }};
            font-weight: {{ block.settings.caption_font.weight }};
            font-style: {{ block.settings.caption_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.author_image_ratio_mobile == "portrait" %}
        <style>
          .image-text-testimonial-image-{{ block.id }} {
            aspect-ratio: 9.6/13;
          }
        </style>
