                      {% elsif block.settings.media_type == 'image' %}            
                        {% if block.settings.image != blank %}
                          {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                            {{ block.settings.image | image_url: width: 700 | image_tag:
                              loading: loading_attribute,
                              sizes: sizes,
                              widths: '165, 360, 535, 750, 1070, 1500'
                            }}
                        {% else %}
                          {{ 'image' | placeholder_svg_tag }}
                        {% endif %}        
                      {% endif %}
                    </div>     
                  {% when 'testimonial' %}
                     <div class="image-text-testimonial-{{ block.id }}">
                        {% if block.settings.text != blank %}
                          <div class="image-text-testimonial-text-{{ block.id }}">{{ block.settings.text }}</div>
                        {% endif %}
                        <div class="image-text-testimonial-image-{{ block.id }}">
                          {% if block.settings.author_image != blank %}
                            {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                              {{ block.settings.author_image | image_url: width: 165 | image_tag:
                                loading: loading_attribute,
                                sizes: sizes,
                                widths: '165, 360, 535, 750, 1070, 1500'
                              }}
                          {% else %}
                            {{ 'image' | placeholder_svg_tag }}
                          {% endif %}  
                        </div>
                        {% if block.settings.author != blank %}
                          <p class="image-text-testimonial-author-{{ block.id }}">{{ block.settings.author }}</p>
                        {% endif %}
                        {% if block.settings.caption != blank %}
                          <p class="image-text-testimonial-caption-{{ block.id }}">{{ block.settings.caption }}</p>
                        {% endif %}
                      </div>
                {% endcase %}
              {% endif %}
            {% endfor %}
