                        </div>     
                      {% elsif block.settings.media_type == 'image' %}            
                        {% if block.settings.image != blank %}
                          {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                            {{ block.settings.image | image_url: width: 700 | image_tag:
                              loading: loading_attribute,
                              sizes: sizes,
                              widths: '165, 360, 535, 750, 1070, 1500'
                            }}
                        {% else %}
                          {{ 'image' | placeholder_svg_tag }}
                        {% endif %}        
                      {% endif %}
                    </div>     
                  {% when 'testimonial' %}
                     <div class="image-text-testimonial-{{ block.id }}">
                        {% if block.settings.text != blank %}
                          <div class="image-text-testimonial-text-{{ block.id }}">{{ block.settings.text }}</div>
                        {% endif %}
                        <div class="image-text-testimonial-image-{{ block.id }}">
                          {% if block.settings.author_image != blank %}
                            {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                              {{ block.settings.author_image | image_url: width: 165 | image_tag:
                                loading: loading_attribute,
                                sizes: sizes,
                                widths: '165, 360, 535, 750, 1070, 1500'
                              }}
                          {% else %}
                            {{ 'image' | placeholder_svg_tag }}
                          {% endif %}  
                        </div>
                        {% if block.settings.author != blank %}
                          <p class="image-text-testimonial-author-{{ block.id }}">{{ block.settings.author }}</p>
                        {% endif %}
                        {% if block.settings.caption != blank %}
                          <p class="image-text-testimonial-caption-{{ block.id }}">{{ block.settings.caption }}</p>
                        {% endif %}
                      </div>
                {% endcase %}
              {% endif %}
            {% endfor %}
          </div>
        </div>
      </div>

    </div>
</div>

{% if lazy %}
<script src="https://cdn.jsdelivr.net/npm/vanilla-lazyload@19.1.3/dist/lazyload.min.js"></script>
{% endif %}

<script>
  function initVideoWithText8() {
    {% if lazy %}
      let lazyLoadInstance = new LazyLoad();

      window.addEventListener(
        "LazyLoad::Initialized",
        function (event) {
          window.lazyLoadInstance = event.detail.instance;
        },
        false
      );
    {% endif %}

    const medias = document.querySelectorAll('.image-text-media-card-{{ section.id }}');

    if(medias) {
      medias.forEach(media => {
        const video = media.querySelector('video');
        const iframe = media.querySelector('iframe');      
        const videoButton = media.querySelector('.image-text-media-play-{{ section.id }}');   
        const muteButton = media.querySelector('.image-text-media-mute-{{ section.id }}'); 

        if(videoButton) {
            videoButton.addEventListener('click', function () {
              setTimeout(() => {
                const isMuted = muteButton?.classList.contains('active');

                if (videoButton.classList.contains('active')) {
                  if (video) video.pause();
                  if (iframe) iframe.src = iframe.getAttribute('data-src3');
                } else {
                  if (video) video.play();
                  if (iframe) {
                    iframe.src = isMuted 
                      ? iframe.getAttribute('data-src1') 
                      : iframe.getAttribute('data-src2');
                  }
                }
                videoButton.classList.toggle('active');
              });
            });
          }   

          if(muteButton) {
            muteButton.addEventListener('click', function() {
              if(muteButton.classList.contains('active')) {
                if(video) {
                  video.muted = true;
                }
                if(iframe) {
                  iframe.src = iframe.getAttribute('data-src2');
                } 
              } else {
                if(video) {
                  video.muted = false;
                }
                if(iframe) {
                  iframe.src = iframe.getAttribute('data-src1');
                }      
              }  
              muteButton.classList.toggle('active'); 
            })
          }

      })
    }
  }

  document.addEventListener('DOMContentLoaded', initVideoWithText8);

  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', initVideoWithText8);
    document.addEventListener('shopify:section:load', initVideoWithText8);
  }
</script>
