{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius
  assign background_style = section.settings.background_style

  assign layout_gap_mobile = section.settings.layout_gap_mobile
  assign layout_gap = section.settings.layout_gap

  assign content_horizontal_align_mobile = section.settings.content_horizontal_align_mobile
  assign content_horizontal_align = section.settings.content_horizontal_align
  assign content_vertical_align = section.settings.content_vertical_align

  assign text_align = ""
  if content_horizontal_align == "start"
    assign text_align = "left"
  elsif content_horizontal_align == "center"
    assign text_align = "center"
  else
    assign text_align = "right"
  endif

  assign text_align_mobile = ""
  if content_horizontal_align_mobile == "start"
    assign text_align_mobile = "left"
  elsif content_horizontal_align_mobile == "center"
    assign text_align_mobile = "center"
  else
    assign text_align_mobile = "right"
  endif

  assign buttons_gap = section.settings.buttons_gap
  assign buttons_gap_mobile = section.settings.buttons_gap_mobile
  assign buttons_mt = section.settings.buttons_mt
  assign buttons_mt_mobile = section.settings.buttons_mt_mobile

  assign open_in_new_tab = section.settings.open_in_new_tab

  assign video = section.settings.video
  assign video_url = section.settings.video_url
  assign source = section.settings.source
  assign video_autoplay = section.settings.video_autoplay
  assign video_loop = section.settings.video_loop
  assign use_controls = section.settings.use_controls
  assign video_position_mobile = section.settings.video_position_mobile
  assign video_position = section.settings.video_position
  assign video_ratio = section.settings.video_ratio
  assign video_ratio_mobile = section.settings.video_ratio_mobile
  assign video_radius = section.settings.video_radius
  assign video_border_thickness = section.settings.video_border_thickness
  assign video_border_color = section.settings.video_border_color

  assign play_icon_size = section.settings.play_icon_size
  assign play_icon_size_mobile = section.settings.play_icon_size_mobile
  assign play_border_thickness = section.settings.play_border_thickness
  assign play_border_color = section.settings.play_border_color
  assign play_border_hover_color = section.settings.play_border_hover_color
  assign play_padding = section.settings.play_padding
  assign play_padding_mobile = section.settings.play_padding_mobile
  assign play_radius =section.settings.play_radius
  assign play_bg_color = section.settings.play_bg_color
  assign play_color = section.settings.play_color
  assign play_bg_hover_color = section.settings.play_bg_hover_color
  assign play_hover_color = section.settings.play_hover_color  
-%}

{%- style -%}
  
  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }
  
  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .video-text-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    gap: {{ layout_gap_mobile }}px;
  }

  .video-text-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: {{ content_horizontal_align_mobile }};
  }

  .video-text-video-{{ section.id }} {
    position: relative;
    overflow: hidden;
    border-radius: {{ video_radius }}px;
    border: {{ video_border_thickness }}px solid {{ video_border_color }};
  }

  .video-text-video-{{ section.id }} video,
  .video-text-video-{{ section.id }} iframe {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    border: 0px;
  }

  .video-text-buttons-{{ section.id }} {
    display: flex;
    align-items: center;
    margin-top: {{ buttons_mt_mobile }}px;
    gap: {{ buttons_gap_mobile }}px;
    flex-wrap: wrap;
    justify-content: {{ content_horizontal_align_mobile }};
  }
  
  .video-with-text-controls-{{ section.id }} {
    display: flex;
    gap: 8px;
    position: absolute;
    top: 16px;
    left: 16px;
  }
  
  .video-with-text-play-{{ section.id }},
  .video-with-text-sound-{{ section.id }} {
    margin: 0;
    cursor: pointer;
    z-index: 2;
    padding: {{ play_padding_mobile }}px;
    border: {{ play_border_thickness }}px solid {{ play_border_color }};
    border-radius: {{ play_radius }}px;
    background-color: {{ play_bg_color }};
    transition: all 0.25s ease 0s;
  }

  .video-with-text-play-{{ section.id }} svg:first-child path,
  .video-with-text-sound-{{ section.id }} svg:first-child path {
    fill: {{ play_color }};
  }

  .video-with-text-play-{{ section.id }} svg:last-child path,
  .video-with-text-sound-{{ section.id }} svg:last-child path {
    fill: {{ play_color }};
  }
  
  .video-with-text-play-{{ section.id }} svg,
  .video-with-text-sound-{{ section.id }} svg {
    display: block;
    width: {{ play_icon_size_mobile }}px;
    height: {{ play_icon_size_mobile }}px;
    object-fit: cover;
  }

  .video-with-text-play-{{ section.id }} svg,
  .video-with-text-sound-{{ section.id }} svg {
    background-color: transparent !important;
  }
  
  .video-with-text-play-{{ section.id }} svg:nth-child(1),
  .video-with-text-sound-{{ section.id }} svg:nth-child(1){
    display: none;
  }

  .video-with-text-play-{{ section.id }}.active svg:nth-child(1),
  .video-with-text-sound-{{ section.id }}.active svg:nth-child(1) {
    display: block;
  }

  .video-with-text-play-{{ section.id }}.active svg:nth-child(2),
  .video-with-text-sound-{{ section.id }}.active svg:nth-child(2) {
    display: none;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }
    
    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .video-text-body-{{ section.id }} {
      gap: {{ layout_gap }}px;
      grid-template-columns: 1fr 1fr;
    }

    .video-text-content-{{ section.id }} {
      justify-content: {{ content_vertical_align }};
      align-items: {{ content_horizontal_align }};
    }

    .video-text-buttons-{{ section.id }} {
      margin-top: {{ buttons_mt }}px;
      gap: {{ buttons_gap }}px;
      justify-content: {{ content_horizontal_align }};
    }

    .video-with-text-play-{{ section.id }},
    .video-with-text-sound-{{ section.id }} {
      padding: {{ play_padding }}px;
    }
    
    .video-with-text-play-{{ section.id }}:hover,
    .video-with-text-sound-{{ section.id }}:hover {
      border: {{ play_border_thickness }}px solid {{ play_border_hover_color }};
      background-color: {{ play_bg_hover_color }};
      transition: all 0.25s ease 0s;
    }
    
    .video-with-text-play-{{ section.id }}:hover svg:first-child path,
    .video-with-text-sound-{{ section.id }}:hover svg:first-child path {
      fill: {{ play_hover_color }};
    }

    .video-with-text-play-{{ section.id }}:hover svg:last-child path,
    .video-with-text-sound-{{ section.id }}:hover svg:last-child path {
      fill: {{ play_hover_color }};
    }

    .video-with-text-play-{{ section.id }} svg,
    .video-with-text-sound-{{ section.id }} svg {
      width: {{ play_icon_size }}px;
      height: {{ play_icon_size }}px;
    }
  }
  
{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }
    
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if video_position_mobile == "top" %}
  <style>
    .video-text-video-{{ section.id }} {
        order: 1;
    }

    .video-text-content-{{ section.id }} {
        order: 2;
    }
  </style>
{% else %}
  <style>
    .video-text-video-{{ section.id }} {
        order: 2;
    }

    .video-text-content-{{ section.id }} {
        order: 1;
    }
  </style>
{% endif %}

{% if video_position == "right" %}
  <style>
    @media(min-width: 1024px) {

      .video-text-video-{{ section.id }} {
          order: 2;
      }

      .video-text-content-{{ section.id }} {
          order: 1;
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {

      .video-text-video-{{ section.id }} {
          order: 1;
      }

      .video-text-content-{{ section.id }} {
          order: 2;
      }
    }
  </style>
{% endif %}

{% if video_ratio_mobile == "portrait" %}
  <style>
    .video-text-video-{{ section.id }} {
      aspect-ratio: 9.6/12; 
    }
  </style>
{% elsif video_ratio_mobile == "landscape" %}
  <style>
    .video-text-video-{{ section.id }} {
      aspect-ratio: 12/9; 
    }
  </style>
{% elsif video_ratio_mobile == "square" %}
  <style>
    .video-text-video-{{ section.id }} {
      aspect-ratio: 12/12; 
    }
  </style>
{% else %}
  {% if source == "uploaded" %}
    <style>
      .video-text-video-{{ section.id }} {
        aspect-ratio: auto; 
      }
    </style>
  {% else %}
    <style>
      .video-text-video-{{ section.id }} {
        aspect-ratio: 16/9; 
      }
    </style>
  {% endif %}
{% endif %}

{% if video_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .video-text-video-{{ section.id }} {
        aspect-ratio: 9.6/12; 
      } 
    }
  </style>
{% elsif video_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .video-text-video-{{ section.id }}{
        aspect-ratio: 12/9; 
      }
    }
  </style>
{% elsif video_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .video-text-video-{{ section.id }} {
        aspect-ratio: 12/12; 
      }
    }
  </style>
{% else %}
  {% if source == "uploaded" %}
    <style>
      @media(min-width: 1024px) {
        .video-text-video-{{ section.id }} {
          aspect-ratio: auto; 
        }
      }
    </style>
  {% else %}
    <style>
      @media(min-width: 1024px) {
        .video-text-video-{{ section.id }} {
          aspect-ratio: 16/9;
        }
      }
    </style>
  {% endif %}
{% endif %}

{% for block in section.blocks %}
    {% if block.type == "socials" %}
      <style>         
        .video-with-social-list-{{ section.id }} {
          padding: 0px;
          margin: 0px;
          list-style: none;
          display: flex;
          flex-wrap: wrap;
          align-items: center;
          justify-content: {{ content_horizontal_align_mobile  }};
          gap: {{ block.settings.social_list_gap_mobile }}px;
          margin-top: {{ block.settings.socials_list_mt_mobile }}px;
        }
      
        .video-with-social-link-{{ section.id }} {          
          box-sizing: content-box;
          transition: all 0.25s ease 0s;
          display: block;       
          width: {{ block.settings.social_icon_size_mobile }}px;
          height: {{ block.settings.social_icon_size_mobile }}px;
        }
      
        .video-with-social-link-{{ section.id }} svg {
          display: block;
          width: 100%;
          height: 100%;
          object-fit: contain;
        }
      
        .video-with-social-link-{{ section.id }} svg path {
          fill: {{ block.settings.social_icon_color }};
          transition: all 0.25s ease 0s;
        }
  
        @media(min-width: 1024px) {  
          .video-with-social-list-{{ section.id }} {
            justify-content: {{ content_horizontal_align }};
            margin-top: {{ block.settings.socials_list_mt }}px;
            gap: {{ block.settings.social_list_gap }}px;
          }
      
          .video-with-social-link-{{ section.id }} {
            width: {{ block.settings.social_icon_size }}px;
            height: {{ block.settings.social_icon_size }}px;
          }
        }        
      </style>

      {% if block.settings.socials_hover == 'color' %}
        <style>
          @media(min-width: 1024px) {
            .video-with-social-item-{{ section.id }}:hover .video-with-social-link-{{ section.id }} {
              background-color: {{ block.settings.social_icon_bg_hover_color }};
              transition: all 0.25s ease 0s;
            }
    
            .video-with-social-item-{{ section.id }}:hover .video-with-social-link-{{ section.id }} svg path {
              fill: {{ block.settings.social_icon_hover_color }};
              transition: all 0.25s ease 0s;
            }   
          }         
        </style>
      {% else %}
        <style>
          @media(min-width: 1024px) {
           .video-with-social-link-{{ section.id }} svg {
              transition: all 0.25s ease 0s;
            }

           .video-with-social-link-{{ section.id }}:hover svg {         
              transform: scale(0.9);
            }
          }
        </style>
      {% endif %}
    {% elsif block.type == "heading" %}
      <style>
        {{ block.settings.heading_font | font_face: font_display: 'swap' }}
        
        .video-text-heading-{{ block.id }} {
          text-align: {{ text_align_mobile }};
          margin-top: {{ block.settings.heading_mt_mobile }}px;
        }

        .video-text-heading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
          font-weight: 700;
        }

        @media(min-width: 1024px) {
            
          .video-text-heading-{{ block.id }} {
            text-align: {{ text_align }};
            margin-top: {{ block.settings.heading_mt }}px;
          }

          .video-text-heading-{{ block.id }} * {
            font-size: {{ block.settings.heading_size }}px;
          }
        }
      </style>

      {% if block.settings.heading_custom %}
        <style>
          .video-text-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}

    {% elsif block.type == "text" %}
      <style>
        {{ block.settings.text_font | font_face: font_display: 'swap' }}
        
        .video-text-text-{{ block.id }} {
          text-align: {{ text_align_mobile }};
          margin-top: {{ block.settings.text_mt_mobile }}px;
        }

        .video-text-text-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
        }

        @media(min-width: 1024px) {
            
          .video-text-text-{{ block.id }} {
            text-align: {{ text_align }};
            margin-top: {{ block.settings.text_mt }}px;
          }

          .video-text-text-{{ block.id }} * {
            font-size: {{ block.settings.text_size }}px;
          }
        }
      </style>

      {% if block.settings.text_custom %}
        <style>
          .video-text-text-{{ block.id }} {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>
      {% endif %}

    {% elsif block.type == "button" %}

      <style>
  
        {{  block.settings.button_font | font_face: font_display: 'swap' }}
        
        .video-text-button-{{ block.id }} {
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 10px;
          width: 100%;
          max-width: fit-content;
          margin: 0;
          font-size: {{ block.settings.button_size_mobile }}px;
          color: {{ block.settings.button_color }};
          line-height: {{ block.settings.button_height }}%;
          text-align: center;
          text-transform: unset;
          text-decoration: none;
          padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
          border-radius: {{ block.settings.button_radius }}px;
          transition: all 0.25s ease 0s;
          position: relative;
          font-weight: 700;
          z-index: 2;
        }
  
        .video-text-button-{{ block.id }}:hover {
          color: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }
  
        .video-text-button-{{ block.id }} svg {
          width: 14px;
          height: 14px;
        }
  
        .video-text-button-{{ block.id }} svg path {
          fill: {{ block.settings.button_color }};
          transition: all 0.25s ease 0s;
        }
  
        .video-text-button-{{ block.id }}:hover svg path {
          fill: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }
  
        @media(min-width: 1024px) {
          .video-text-button-{{ block.id }} {
            padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
            font-size: {{ block.settings.button_size }}px;
          }
        }
      </style>
  
      {% if block.settings.button_custom %}
        <style>
          .video-text-button-{{ block.id }} {
            font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
            font-weight: {{ block.settings.button_font.weight }};
            font-style: {{ block.settings.button_font.style }};
          }
        </style>
      {% endif %}
  
      {% if block.settings.button_style == "non_outline" or block.settings.button_style == "non_outline_arrow" %}
        <style>
          .video-text-button-{{ block.id }} {
            background-color: {{ block.settings.button_bg_color }};
          }

          .video-text-button-{{ block.id }}:hover {
            background-color: {{ block.settings.button_bg_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "outline" or block.settings.button_style == "outline_arrow" %}
        <style>
          .video-text-button-{{ block.id }} {
            background-color: {{ block.settings.button_bg_color }};
            border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_color }};
          }

          .video-text-button-{{ block.id }}:hover {
            background-color: {{ block.settings.button_bg_hover_color }};
            border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "link" %}
        <style>
          .video-text-button-{{ block.id }} {
            max-width: fit-content;
            padding: 0px;
            text-decoration: underline;
          }
        </style>
      {% endif %}
        
    {% endif %}

{% endfor %}


<div class="section-{{ section.id }} video-text-{{ section.id }}" {% if background_style == 'solid' %}style="background-color:{{ background_color }};"{% elsif background_style == 'gradient' %}style="background-image: {{ background_gradient }};"{% endif %}>
    <div class="section-{{ section.id }}-settings">
      <div class="video-text-body-{{ section.id }}">
        <div class="video-text-content-{{ section.id }}">
          {% assign button_open = false %}        
          {% for block in section.blocks %}
              {% if block.type == 'socials' %}
                <ul class="video-with-social-list-{{ section.id }}">
                  {% if block.settings.instagram != blank %}
                    <li class="video-with-social-item-{{ section.id }}">
