{%- style -%}
  .section-{{ section.id }}-padding {
    max-width: {{section.settings.section-width}}rem;
    margin: 0 auto;
    padding: 0 1.5rem;
    padding-top: {{ section.settings.padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ section.settings.padding_bottom | times: 0.75 | round: 0 }}px;
  }

  @media screen and (min-width: 750px) {
    .section-{{ section.id }}-padding {
      padding: 0 5rem;
      padding-top: {{ section.settings.padding_top }}px;
      padding-bottom: {{ section.settings.padding_bottom }}px;
    }
  }
{%- endstyle -%}

<style>

.testimonials-section .swiper-slide {
  display: flex !important;
  height:auto;
  align-self: center;
}

.testimonials-section{
  	position:relative;
}
.testimonials-section .page-width{
    margin:auto;
	max-width:1200px;
  	position:relative;
}
.testimonials-title {
	text-align: center;
	margin-bottom: 20px;
}
.testimonials-title .title {
	margin-top: 0;
	margin-bottom: 10px;
	font-style: normal;
	font-weight: 800;
	letter-spacing: .04em;
	line-height: 41px;
	text-transform: uppercase;
}
.title-rating > span.ratings {
	margin-right: 10px;
}
.title-rating > span.ratings i {
	display: inline-flex;
}
.title-rating > span.ratings i + i{
	margin-left: 2px;
}
.title-rating > span:not(.ratings) {
	font-style: normal;
	font-weight: 400;
	font-size: 16px;
	line-height: 17px;
	color: inherit;
	display: inline-block;
}
.review-box {
	border: 3px solid;
	border-radius: 27px;
	text-align: center;
	padding: 40px 30px;
}
.review-box .review-title{
    font-style: normal;
    font-weight: 700;
}
.review-box .review-desc{
    font-style: normal;
    font-weight: 400;
}
.review-box .author-name {
	font-style: normal;
	font-weight: 700;
	margin-bottom: 6px;
}

.review-slide-{{ section.id }} .swiper-pagination {
	position: relative;
	margin-top: 20px;
	bottom: unset !important;
}
.review-slide-{{ section.id }} .swiper-pagination .swiper-pagination-bullet {
	width: 15px;
	height: 15px;
  	opacity: 1;
  	background: {{section.settings.nonactive-pagination-color}};
}
.review-slide-{{ section.id }} .swiper-pagination .swiper-pagination-bullet-active{
	background: {{section.settings.active-pagination-color}};
}
.review-slide-arrow-{{ section.id }} .swiper-button-next, .review-slide-arrow-{{ section.id }} .swiper-button-prev {
	width: 101px;
	height: 53px;
	top: unset;
	bottom: 40%;
}
.review-slide-arrow-{{ section.id }} .swiper-button-next::after, 
.review-slide-arrow-{{ section.id }} .swiper-button-prev::after {
	content: none;
}
.review-slide-arrow-{{ section.id }} .swiper-button-next{
	right: -60px;
}
.review-slide-arrow-{{ section.id }} .swiper-button-prev{
	left: -60px;
}
.review-slide-arrow-{{ section.id }} .swiper-button-prev > svg,
.review-slide-arrow-{{ section.id }} .swiper-button-next > svg{
  width: 100%;
  height: 100%;
  display: block;
  object-fit: contain;
}
.review-slide-arrow-{{ section.id }} .swiper-button-prev > svg {
  transform: rotate(210deg);
}
.review-slide-arrow-{{ section.id }} .swiper-button-next > svg {
  transform: rotate(30deg);
}

@media only screen and (max-width:1439px){
  .review-padd {
    padding: 0px 50px;
  }
  .review-slide-arrow-{{ section.id }} .swiper-button-prev {
    left: 20px;
    width: 70px;
    height: 36px;
  }
  .review-slide-arrow-{{ section.id }} .swiper-button-next{
  	right: 20px;
    width: 70px;
    height: 36px;
  }
}
@media only screen and (max-width:767px){
  .review-padd {
    padding: 0 90px;
    position: relative;
  }
  .review-slide-arrow-{{ section.id }} .swiper-button-next{
    right: 35px;
  }
  .review-slide-arrow-{{ section.id }} .swiper-button-prev {
    left: 35px;
  }
  .review-slide-arrow-{{ section.id }} .swiper-button-next, .review-slide-arrow-{{ section.id }} .swiper-button-prev {
    top: unset;
    bottom: calc(50% - 2px);
    width: 45px;
    height: 24px;
  }
  .testimonials-section {

    position: relative;
  }
  .testimonials-title {
    margin-bottom: 30px;
  }
  .testimonials-title .title {
    line-height: 34px;
    margin-bottom: 20px;
  }
}
@media only screen and (max-width:479px){
  .review-padd {
    padding: 0 0px;
    position: relative;
  }
  .review-slide-arrow-{{ section.id }} .swiper-button-next, .review-slide-arrow-{{ section.id }} .swiper-button-prev{
    display:none;
  }
}

