<script>
  function inintEcoTestimonials() {
    var swiper_testimonial_eco = new Swiper(".review-slide-{{ section.id }}", {
        slidesPerView: 3,
        spaceBetween: 30,
        loop: true,
        navigation: {
          nextEl: ".swiper-button-next-{{ section.id }}",
          prevEl: ".swiper-button-prev-{{ section.id }}",
        },
        pagination: {
          el: ".swiper-pagination-{{ section.id }}",
          clickable: true,
        },
        breakpoints: {
          0: {
            slidesPerView: 1,
            spaceBetween: 10,
          },
          768: {
            slidesPerView: 2,
            spaceBetween: 30,
          },
          1025: {
            slidesPerView: 3,
            spaceBetween: 30,
          }
        }
      });
    console.log('eco testi loaded')
  }
   document.addEventListener('DOMContentLoaded', inintEcoTestimonials);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', inintEcoTestimonials);
     document.addEventListener('shopify:section:load', inintEcoTestimonials);
  }
</script>
