{% style %}

{{ section.settings.type_header_font | font_face: font_display: 'swap' }}

.feature-type3 {
    font-family: {{section.settings.type_header_font.family}};
    font-weight: {{section.settings.type_header_font.weight}};
    margin: 0;
    padding: 50px 0;
}
.feature-type3 .container {
    max-width: {{section.settings.max_container_width}};
    margin: 0 auto;
}
.feature-type3 .box__title {
    font-size: {{ section.settings.feature_title_font_size }}px;
    color: {{ section.settings.feature_title_color }};
}
.feature-type3 .box__description {
    font-size: {{ section.settings.feature_text_font_size }}px;
    color: {{ section.settings.feature_text_color }};
}
@media (max-width: 768px) {
    .feature-type3 .box__title {
        font-size: {{ section.settings.feature_title_font_mob_size }}px;
    }
    .feature-type3 .box__description {
        font-size: {{ section.settings.feature_text_font_mob_size }}px;
    }
}


  .feature-type3 .wrapper-box {
    background-color: {{ section.settings.section_bg_color }}; 
    border: 1px solid {{section.settings.section_border_color}};
    box-shadow: 4px 4px 20px rgba(196, 196, 196, 0.04);
    border-radius: 15px;
      padding: 46px 20px 47px 56px;
      display: flex;
      justify-content: space-between;
  }
  .feature-type3 .box:not(:nth-child(2)) .box__content-wrapper {
      padding-right: 10px;
  }
  .feature-type3 .box {
      display: flex;
      align-items: center;
      flex: 0 0 30%;
      position: relative;
  }
  .feature-type3 .box:nth-child(2) {
      flex: 0 0 33%;
      padding: 0 4%;
      border-left: 1px solid {{section.settings.section_vl_color}};
      border-right: 1px solid {{section.settings.section_vl_color}};
  }

  .feature-type3 .box__title {
    font-weight: bold;
  }
  .feature-type3 .box__image {
    margin-right: 30px;
  }

  .feature-type3 .box__image img {
    width: {{block.settings.feature_image_width}};
  }

  @media (max-width: 1024px) {
      .feature-type3 .container {
          padding: 0 15px
      }
  }

  @media (max-width: 768px) {
      .feature-type3 .wrapper-box {
          flex-direction: column;
      }

      .feature-type3 .wrapper-box {
          padding: 23px 10px 20px 20px;
      }

      .feature-type3 .box {
          flex: 0 0 100%;
          position: relative;
          margin-bottom: 40px;
      }

      .feature-type3 .box:last-child {
          margin-bottom: 0;
      }

      .feature-type3 .box:nth-child(2) {
          flex: 0 0 100%;
          padding: 0;
          border-left: none;
          border-right: none;
      }
  }
{% endstyle %}

<div class="container">
    <div class="wrapper-box">
        {%- for block in section.blocks -%}
            <div class="box">
                <div class="box__image" style="width: {{block.settings.feature_image_width}};">
                    {%- if block.settings.feature_image and block.settings.feature_use_svg == false -%}
                        {{ block.settings.feature_image | image_url: width: block.settings.feature_image_width | image_tag:  alt: section.settings.feature_title }}
                    {%- elsif  block.settings.feature_use_svg == true -%}
                        {{ block.settings.feature_svg_code }}
                    {%- else -%}
                        {{ 'image' | placeholder_svg_tag }}
                    {%- endif -%}
                </div>
                <div class="box__content-wrapper">
                    <div class="box__title">{{ block.settings.feature_title }}</div>
                    <div class="box__description">{{ block.settings.feature_text }}</div>
                </div>
            </div>
        {%- endfor -%}
    </div>
</div>
