liquid_code:
{% comment %}
  Section: {{title}}
  Description: {{description}}
{% endcomment %}

<div class="hero-section" style="background: linear-gradient(135deg, {{bg_color_1}} 0%, {{bg_color_2}} 100%); padding: 100px 20px; text-align: center;">
  <div class="container" style="max-width: 800px; margin: 0 auto;">
    <h1 style="color: {{heading_color}}; font-size: 56px; font-weight: bold; margin-bottom: 20px; line-height: 1.2;">{{heading}}</h1>
    <p style="color: {{subheading_color}}; font-size: 22px; margin-bottom: 40px; line-height: 1.6;">{{subheading}}</p>
    <a href="{{cta_url}}" class="cta-button" style="background-color: {{button_color}}; color: {{button_text_color}}; padding: 18px 40px; text-decoration: none; border-radius: 8px; display: inline-block; font-size: 18px; font-weight: 600; transition: transform 0.2s;">{{cta_text}}</a>
  </div>
</div>
