liquid_code:
{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy

  assign heading = section.settings.heading
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_color = section.settings.heading_color
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height
  assign heading_align = section.settings.heading_align

  assign grid_mt = section.settings.grid_mt
  assign grid_gap = section.settings.grid_gap
  assign grid_gap_mobile = section.settings.grid_gap_mobile

  assign text = section.settings.text
  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_color = section.settings.text_color
  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_height = section.settings.text_height
  assign text_align = section.settings.text_align

  assign brands_custom = section.settings.brands_custom
  assign brands_size = section.settings.brands_size
  assign brands_size_mobile = section.settings.brands_size_mobile
  assign brands_gap = section.settings.brands_gap
  assign brands_gap_mobile = section.settings.brands_gap_mobile
  assign brands_mt = section.settings.brands_mt
  assign brands_align = section.settings.brands_align

  assign left_image = section.settings.left_image
  assign left_image_url = section.settings.left_image_url
  assign left_image_radius = section.settings.left_image_radius
  assign left_image_border_thickness = section.settings.left_image_border_thickness
  assign left_image_border_color = section.settings.left_image_border_color
  assign left_image_ratio = section.settings.left_image_ratio
  assign left_image_mt = section.settings.left_image_mt
  assign left_image_width = section.settings.left_image_width
  assign left_image_width_mobile = section.settings.left_image_width_mobile
  assign left_image_align = section.settings.left_image_align

  assign right_image = section.settings.right_image
  assign right_image_url = section.settings.right_image_url
  assign right_image_radius = section.settings.right_image_radius
  assign right_image_border_thickness = section.settings.right_image_border_thickness
  assign right_image_border_color = section.settings.right_image_border_color
  assign right_image_ratio = section.settings.right_image_ratio
  assign right_image_hide = section.settings.right_image_hide
  assign right_image_width = section.settings.right_image_width
  assign right_image_width_mobile = section.settings.right_image_width_mobile
  assign right_image_align = section.settings.right_image_align
-%}

{%- style -%}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .image-text-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    text-align: {{ heading_align }};
  }

  .image-text-grid-{{ section.id }} {
    margin-top: {{ grid_mt | times: 0.75 | round: 0 }}px;
    gap: {{ grid_gap_mobile }}px;
    display: grid;
    grid-template-columns: repeat(1, 1fr);
  }

  .image-text-content-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    text-align: {{ text_align }};
  }

  .image-text-brands-{{ section.id }} {
    margin-top: {{ brands_mt | times: 0.75 | round: 0 }}px;
    display: flex;
    align-items: center;
    justify-content: {{ brands_align }};
    gap: {{ brands_gap_mobile }}px;
    flex-wrap: wrap;
  }

  .image-text-brand-{{ section.id }} svg {
    background-color: #DDDDDD;
    border-radius: 50%;
  }

  .image-text-left-image-{{ section.id }} {
    display: block;
    padding-right: 30px;
    width: 100%;
    max-width: {{ left_image_width_mobile }}%;
    margin-top: {{ left_image_mt | times: 0.75 | round: 0 }}px;
    border-radius: {{ left_image_radius }}px;
    border: {{ left_image_border_thickness }}px solid {{ left_image_border_color }};
  }

  .image-text-right-image-{{ section.id }} {
    display: block;
    padding-left: 30px;
    width: 100%;
    max-width: {{ right_image_width_mobile }}%;
    border-radius: {{ right_image_radius }}px;
    border: {{ right_image_border_thickness }}px solid {{ right_image_border_color }};
  }

  .image-text-left-image-{{ section.id }} img,
  .image-text-left-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: {{ left_image_radius }}px;
  }

  .image-text-right-image-{{ section.id }} img,
  .image-text-right-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: {{ right_image_radius }}px;
  }

  .image-text-left-image-{{ section.id }} svg,
  .image-text-right-image-{{ section.id }} svg {
    background-color: #DDDDDD;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .image-text-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .image-text-grid-{{ section.id }} {
      margin-top: {{ grid_mt }}px;
      gap: {{ grid_gap }}px;
      grid-template-columns: repeat({% if right_image_hide %}1{% else %}2{% endif %}, 1fr);
    }

    .image-text-content-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .image-text-brands-{{ section.id }} {
      margin-top: {{ brands_mt }}px;
      gap: {{ brands_gap }}px;
    }

    .image-text-left-image-{{ section.id }} {
      max-width: {{ left_image_width }}%;
      margin-top: {{ left_image_mt }}px;
      padding-right: 0px;
    }

    .image-text-right-image-{{ section.id }} {
      max-width: {{ right_image_width }}%;
      padding-left: 0px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .image-text-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .image-text-content-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if brands_custom %}
  <style>
    .image-text-brand-{{ section.id }} {
      height: {{ brands_size_mobile }}px;
      width: {{ brands_size_mobile }}px;
    }

    .image-text-brand-{{ section.id }} img,
    .image-text-brand-{{ section.id }} svg {
      display: block;
      width: 100%;
      height: 100%;
      object-fit: contain;
    }

    @media(min-width: 1024px) {
      .image-text-brand-{{ section.id }} {
        height: {{ brands_size }}px;
        width: {{ brands_size }}px;
      }
    }
  </style>
{% endif %}

{% if left_image_ratio == "portrait" %}
  <style>
    .image-text-left-image-{{ section.id }} {
      aspect-ratio: 1/1.4;
    }
  </style>
{% elsif left_image_ratio == "square" %}
  <style>
    .image-text-left-image-{{ section.id }} {
      aspect-ratio: 1/1;
    }
  </style>
{% elsif left_image_ratio == "landscape" %}
  <style>
    .image-text-left-image-{{ section.id }} {
      aspect-ratio: 1.4/1;
    }
  </style>
{% endif %}

{% if right_image_ratio == "portrait" %}
  <style>
    .image-text-right-image-{{ section.id }} {
      aspect-ratio: 1/1.4;
    }
  </style>
{% elsif right_image_ratio == "square" %}
  <style>
    .image-text-right-image-{{ section.id }} {
      aspect-ratio: 1/1;
    }
  </style>
{% elsif right_image_ratio == "landscape" %}
  <style>
    .image-text-right-image-{{ section.id }} {
      aspect-ratio: 1.4/1;
    }
  </style>
{% endif %}

{% if left_image_align == "center" %}
  <style>
    .image-text-left-image-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    }
  </style>
{% elsif left_image_align == "right" %}
  <style>
    .image-text-left-image-{{ section.id }} {
      margin-left: auto;
    }
  </style>
{% endif %}

{% if right_image_align == "center" %}
  <style>
    .image-text-right-image-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    }
  </style>
{% elsif right_image_align == "right" %}
  <style>
    .image-text-right-image-{{ section.id }} {
      margin-left: auto;
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} image-text-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings ">
      {% if heading != blank %}
        <div class="image-text-heading-{{ section.id }}">
          {{ heading }}
        </div>
      {% endif %}
      <div class="image-text-grid-{{ section.id }}">
        <div class="image-text-left-{{ section.id }}">
          {% if text != blank %}
            <div class="image-text-content-{{ section.id }}">
              {{ text }}
            </div>
          {% endif %}
          {% if section.blocks.size > 0 %}
            <div class="image-text-brands-{{ section.id }}">
              {% for block in section.blocks %}
                <{% if block.settings.image_url != blank %}a href="{{ block.settings.image_url }}" {% else %}div{% endif %} class="image-text-brand-{{ section.id }}">
                  {% if block.settings.image != blank %}
                    <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                  {% else %}
                    {{ 'image' | placeholder_svg_tag }}
                  {% endif %}
                </{% if block.settings.image_url != blank %}a{% else %}div{% endif %}>
              {% endfor %}
            </div>
          {% endif %}
          <{% if left_image_url != blank %}a href="{{ left_image_url }}" {% else %}div{% endif %} class="image-text-left-image-{{ section.id }}">
            {% if left_image != blank %}
              <img src="{{ left_image | image_url }}" alt="{{ left_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
            {% else %}
              {{ 'image' | placeholder_svg_tag }}
            {% endif %}
          </{% if left_image_url != blank %}a{% else %}div{% endif %}>
        </div>
        {% unless right_image_hide %}
        <div class="image-text-right-{{ section.id }}">
          <{% if right_image_url != blank %}a href="{{ right_image_url }}" {% else %}div{% endif %} class="image-text-right-image-{{ section.id }}">
            {% if right_image != blank %}
              <img src="{{ right_image | image_url }}" alt="{{ right_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
            {% else %}
              {{ 'image' | placeholder_svg_tag }}
            {% endif %}
          </{% if right_image_url != blank %}a{% else %}div{% endif %}>
        </div>
        {% endunless %}
      </div>
    </div>
</div>
