    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .media-grid-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if item_shadow %}
  <style>
    .media-grid-item-{{ section.id }} {
      box-shadow: {{ item_shadow_color | hex_to_rgba: 0.15 }} 0px 2px 8px;
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  <style>
    .media-grid-item-{{ block.id }} {
      display: flex;
      position: relative;
      height: 100%;
      grid-area: span min(1, {{ block.settings.row_width }}) / span min(1,{{ block.settings.column_width }});
      text-decoration: none;
      overflow: hidden;
    }

    .media-grid-item-{{ block.id }}::after {
      content: '';
      position: absolute;
      left: 0;
      right: 0;
      top: 0;
      bottom: 0;
      background-color: {{ overlay_color }};
      z-index: 1;
      opacity: {{ block.settings.bg_overlay }}%;
    }

    .media-grid-subheading-{{ block.id }} * {
      color: {{ block.settings.sub_heading_color }};
    }

    .media-grid-title-{{ block.id }} * {
      color: {{ block.settings.title_color }};
    }

    .media-grid-text-{{ block.id }} * {
      color: {{ block.settings.text_color }};
    }

    .media-grid-button-{{ block.id }} {
      color: {{ block.settings.button_color }};
    }

    .media-grid-button-{{ block.id }}:hover {
      color: {{ block.settings.button_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .media-grid-button-{{ block.id }} svg path {
      fill: {{ block.settings.button_color }};
      transition: all 0.25s ease 0s;
    }

    .media-grid-button-{{ block.id }}:hover svg path {
      fill: {{ block.settings.button_hover_color }};
      transition: all 0.25s ease 0s;
    }

    @media(min-width: 1024px) {
      .media-grid-item-{{ block.id }} {
        grid-area: span min(2, {{ block.settings.row_width }}) / span min(2,{{ block.settings.column_width }});
      }
    }

  </style>

  {% if block.settings.content_align_mobile == "top-left" %}
    <style>
      .media-grid-item-{{ block.id }} {
        align-items: start;
        justify-content: start;
      }
    </style>

  {% elsif block.settings.content_align_mobile == "top-center" %}
