liquid_code:
{% comment %}
  Section: {{title}}
  Description: {{description}}
{% endcomment %}

<section class="product-grid-section" style="padding: 60px 20px; background-color: {{bg_color}};">
  <div class="container" style="max-width: 1200px; margin: 0 auto;">
    {% if {{heading}} != '' %}
      <h2 style="text-align: center; color: {{heading_color}}; font-size: {{heading_size}}px; margin-bottom: 20px; font-weight: bold;">{{heading}}</h2>
    {% endif %}

    {% if {{subheading}} != '' %}
      <p style="text-align: center; color: {{subheading_color}}; font-size: 18px; margin-bottom: 50px; max-width: 600px; margin-left: auto; margin-right: auto;">{{subheading}}</p>
    {% endif %}

    <div class="products-grid" style="display: grid; grid-template-columns: repeat(auto-fill, minmax({{product_width}}px, 1fr)); gap: {{grid_gap}}px;">
      {% for product in collections.{{collection_handle}}.products limit: {{product_limit}} %}
        <div class="product-card" style="background: {{card_bg}}; border-radius: {{border_radius}}px; overflow: hidden; box-shadow: 0 2px 8px rgba(0,0,0,0.1); transition: transform 0.3s ease, box-shadow 0.3s ease;">
          <a href="{{ product.url }}" style="text-decoration: none; color: inherit;">
            <div class="product-image" style="position: relative; overflow: hidden; background: #f5f5f5;">
              <img 
                src="{{ product.featured_image | img_url: '600x600' }}" 
                alt="{{ product.title }}" 
                style="width: 100%; height: {{image_height}}px; object-fit: cover; transition: transform 0.3s ease;"
                loading="lazy"
              >
            </div>
            <div style="padding: {{card_padding}}px;">
              <h3 style="margin: 0 0 10px 0; color: {{title_color}}; font-size: 18px; font-weight: 600; line-height: 1.4;">{{ product.title }}</h3>
              <p style="color: {{price_color}}; font-size: 20px; font-weight: bold; margin: 0;">{{ product.price | money }}</p>
              {% if product.compare_at_price > product.price %}
                <p style="color: {{compare_price_color}}; font-size: 14px; text-decoration: line-through; margin: 5px 0 0 0;">{{ product.compare_at_price | money }}</p>
              {% endif %}
            </div>
          </a>
        </div>
      {% endfor %}
    </div>

    {% if {{show_view_all}} == 'true' %}
      <div style="text-align: center; margin-top: 40px;">
        <a href="/collections/{{collection_handle}}" style="display: inline-block; background-color: {{button_color}}; color: {{button_text_color}}; padding: 15px 40px; text-decoration: none; border-radius: 8px; font-weight: 600; font-size: 16px;">{{button_text}}</a>
      </div>
    {% endif %}
  </div>
</section>

<style>
  .product-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 4px 16px rgba(0,0,0,0.15);
  }

  .product-card:hover .product-image img {
    transform: scale(1.05);
  }
</style>
