{%- style -%}

  {{ heading_font | font_face: font_display: 'swap' }}
  {{ table_heading_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .product-other-heading-{{ section.id }} {
    margin: 0;
    font-size: {{ heading_size | times: 0.85 }}px;
    font-weight: 700;
    font-style: italic;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    text-align: {{ heading_align }};
    display: block;
    width: 100%;
  }

  .product-other-body-{{ section.id }} {
    margin-top: {{ table_mt | times: 0.75 | round: 0 }}px;
  }

  .product-other-grid-{{ section.id }} {
    display: grid;
    {% if third_heading != blank %}
      grid-template-columns: 29% 24% 23.5% 23.5%;
    {% else %}
      grid-template-columns: 40% 30% 30%;
    {% endif %}
  }

  .product-other-item-{{ section.id }} {
    display: flex !important;
    align-items: center;
    justify-content: center;
  }

  .product-other-item-{{ section.id }}.active {
    background-color: {{ table_column_active_bg_color }};
  }

  .product-other-grid-header-{{ section.id }} .product-other-item-{{ section.id }} {
    padding: {{ table_heading_padding_vertical_mobile }}px {{ table_heading_padding_horizontal_mobile }}px;
  }

  .product-other-grid-header-{{ section.id }} .product-other-item-{{ section.id }}.active {
    border-top-left-radius: {{ table_column_active_radius }}px;
    border-top-right-radius: {{ table_column_active_radius }}px;
  }

  .product-other-grid-header-{{ section.id }} .product-other-item-{{ section.id }} h3 {
    margin: 0;
    font-size: {{ table_heading_size_mobile }}px;
    color: {{ table_heading_color }};
    line-height: {{ table_heading_height }}%;
    text-transform: unset;
    text-align: {{ table_heading_align }};
    display: block;
    width: 100%;
    font-weight: 700;
  }

  .product-other-grid-header-{{ section.id }} .product-other-item-{{ section.id }}.active h3 {
    color: {{ table_heading_active_color }};
  }

  .product-other-grid-header-{{ section.id }} + .product-other-grid-{{ section.id }} .product-other-item-{{ section.id }}:first-child {
    border-top-left-radius: {{ table_radius }}px;
  }

  .product-other-grid-header-{{ section.id }} + .product-other-grid-{{ section.id }} .product-other-item-{{ section.id }}:last-child {
    border-top-right-radius: {{ table_radius }}px;
  }

  .product-other-grid-header-{{ section.id }} + .product-other-grid-{{ section.id }} .product-other-item-{{ section.id }}.active:last-child {
    border-top-right-radius: 0px;
  }

  .product-other-grid-rows-{{ section.id }} .product-other-item-{{ section.id }} {
    padding: {{ table_row_padding_vertical_mobile }}px {{ table_row_padding_horizontal_mobile }}px;
    border-top: {{ table_border_thickness }}px solid {{ table_border_color }};
  }

  .product-other-grid-rows-{{ section.id }} .product-other-item-{{ section.id }}:first-child {
    border-left: {{ table_border_thickness }}px solid {{ table_border_color }};
  }

  .product-other-grid-rows-{{ section.id }} .product-other-item-{{ section.id }}:last-child {
    border-right: {{ table_border_thickness }}px solid {{ table_border_color }};
  }

  .product-other-grid-rows-{{ section.id }} .product-other-item-{{ section.id }}.active {
    position: relative;
    border-color: {{ table_column_active_bg_color }};
  }

  .product-other-grid-rows-{{ section.id }} .product-other-item-{{ section.id }}.active::after {
    content: '';
    position: absolute;
    width: 100%;
    height: 15px;
    left: 0;
    bottom: -15px;
    background-color: {{ table_column_active_bg_color }};
    border-bottom-left-radius: {{ table_column_active_radius }}px;
    border-bottom-right-radius: {{ table_column_active_radius }}px;
  }

  .product-other-grid-rows-{{ section.id }}:last-child .product-other-item-{{ section.id }} {
    border-bottom: {{ table_border_thickness }}px solid {{ table_border_color }};
  }

  .product-other-grid-rows-{{ section.id }}:last-child .product-other-item-{{ section.id }}.active {
     border-color: {{ table_column_active_bg_color }};
  }

  .product-other-grid-rows-{{ section.id }}:last-child .product-other-item-{{ section.id }}:first-child {
    border-bottom-left-radius: {{ table_radius }}px;
  }

  .product-other-grid-rows-{{ section.id }}:last-child .product-other-item-{{ section.id }}:last-child {
    border-bottom-right-radius: {{ table_radius }}px;
  }

  .product-other-grid-rows-{{ section.id }}:last-child .product-other-item-{{ section.id }}.active:last-child {
    border-bottom-right-radius: 0px;
  }
  .product-other-grid-rows-{{ section.id }}:last-child .product-other-item-{{ section.id }}.active:last-child::after {
    border-right: {{ table_border_thickness }}px solid {{ table_column_active_bg_color }};
    box-sizing: content-box;
  }

  .product-other-grid-rows-{{ section.id }} .product-other-item-{{ section.id }}:first-child h3 {
    margin: 0;
    font-size: {{ table_row_heading_size_mobile }}px;
    color: {{ table_row_heading_color }};
    line-height: {{ table_row_heading_height }}%;
    text-transform: unset;
    text-align: {{ table_row_heading_align }};
    display: block;
    width: 100%;
    font-weight: 700;
  }

  .product-other-icon-{{ section.id }} {
    width: {{ table_icon_size_mobile }}px;
    height: {{ table_icon_size_mobile }}px;
  }

  .product-other-icon-{{ section.id }}.icon-yes-{{ section.id }} svg path,
  .product-other-icon-{{ section.id }}.icon-no-{{ section.id }} svg path{
    fill: {{ table_icon_color }};
  }

  .product-other-item-{{ section.id }}.active .product-other-icon-{{ section.id }}.icon-yes-{{ section.id }} svg path,
  .product-other-item-{{ section.id }}.active .product-other-icon-{{ section.id }}.icon-no-{{ section.id }} svg path {
    fill: {{ table_icon_active_color }};
  }

  .product-other-icon-{{ section.id }} svg,
  .product-other-icon-{{ section.id }} img {
    display: block;
    object-fit: contain;
    width: 100%;
    height: 100%;
  }

  @media(min-width: 350px) {
    .product-other-grid-{{ section.id }} {
      {% if third_heading != blank %}
        grid-template-columns: 38% 22% 20% 20%;
      {% else %}
        grid-template-columns: 40% 30% 30%;
      {% endif %}
    }
  }
  @media(min-width: 768px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .product-other-grid-{{ section.id }} {
      {% if third_heading != blank %}
        grid-template-columns: 30% 22% 24% 24%;
      {% else %}
        grid-template-columns: 40% 28% 32%;
      {% endif %}
    }

    .product-other-heading-{{ section.id }} {
      font-size: {{ heading_size }}px;
    }

    .product-other-body-{{ section.id }} {
      margin-top: {{ table_mt }}px;
    }

    .product-other-grid-header-{{ section.id }} .product-other-item-{{ section.id }} {
      padding: {{ table_heading_padding_vertical }}px {{ table_heading_padding_horizontal }}px;
    }

    .product-other-grid-header-{{ section.id }} .product-other-item-{{ section.id }} h3 {
      font-size: {{ table_heading_size }}px;
    }

    .product-other-grid-rows-{{ section.id }} .product-other-item-{{ section.id }} {
      padding: {{ table_row_padding_vertical }}px {{ table_row_padding_horizontal }}px 
    }

    .product-other-grid-rows-{{ section.id }} .product-other-item-{{ section.id }}.active::after {
      height: 25px;
      bottom: -25px;
    }

    .product-other-grid-rows-{{ section.id }} .product-other-item-{{ section.id }}:first-child h3 {
      font-size: {{ table_row_heading_size }}px;
    }

    .product-other-icon-{{ section.id }} {
      width: {{ table_icon_size }}px;
      height: {{ table_icon_size }}px;
    }

  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .product-other-heading-{{ section.id }} {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if table_heading_custom %}
  <style>
    .product-other-grid-header-{{ section.id }} .product-other-item-{{ section.id }} h3 {
      font-family: {{ table_heading_font.family }}, {{ table_heading_font.fallback_families }};
      font-weight: {{ table_heading_font.weight }};
      font-style: {{ table_heading_font.style }};
    }
  </style>
{% endif %}

{% if table_row_heading_custom %}
  <style>
    .product-other-grid-rows-{{ section.id }} .product-other-item-{{ section.id }}:first-child h3 {
      font-family: {{ table_row_heading_font.family }}, {{ table_row_heading_font.fallback_families }};
      font-weight: {{ table_row_heading_font.weight }};
      font-style: {{ table_row_heading_font.style }};
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} product-other-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings ">
      {% if heading != blank %}
        <h2 class="product-other-heading-{{ section.id }}">{{ heading }}</h2>
      {% endif %}
      <div class="product-other-body-{{ section.id }}">
        <div class="product-other-grid-{{ section.id }} product-other-grid-header-{{ section.id }}">
          <div class="product-other-item-{{ section.id }}"></div>
          <div class="product-other-item-{{ section.id }} {% if table_column_active == "1" %}active{% endif %}">
            <h3>{{ first_heading }}</h3>
          </div>
          <div class="product-other-item-{{ section.id }} {% if table_column_active == "2" %}active{% endif %}">
            <h3>{{ second_heading }}</h3>
          </div>
          {% if third_heading != blank %}
            <div class="product-other-item-{{ section.id }} {% if table_column_active == "3" %}active{% endif %}">
              <h3>{{ third_heading }}</h3>
            </div>
          {% endif %}
        </div>
        {% for block in section.blocks %}
          <div class="product-other-grid-{{ section.id }} product-other-grid-rows-{{ section.id }}">
            <div class="product-other-item-{{ section.id }}">
              <h3>{{ block.settings.row_heading }}</h3>
            </div>
            <div class="product-other-item-{{ section.id }} {% if table_column_active == "1" %}active{% endif %}">
              <div class="product-other-icon-{{ section.id }} icon-{{ block.settings.first_icon }}-{{ section.id }}">
                {% if block.settings.first_icon == "yes" %}
                  {{ icon_yes }}
                {% elsif block.settings.first_icon == "no"  %}
                  {{ icon_no }}
                {% else %}
                  {{ icon_dont_know }}
                {% endif %}
              </div>
            </div>
            <div class="product-other-item-{{ section.id }} {% if table_column_active == "2" %}active{% endif %}">
              <div class="product-other-icon-{{ section.id }} icon-{{ block.settings.second_icon }}-{{ section.id }}">
                {% if block.settings.second_icon == "yes" %}
                  {{ icon_yes }}
                {% elsif block.settings.second_icon == "no"  %}
                  {{ icon_no }}
                {% else %}
                  {{ icon_dont_know }}
                {% endif %}
              </div>
            </div>
            {% if third_heading != blank %}
              <div class="product-other-item-{{ section.id }} {% if table_column_active == "3" %}active{% endif %}">
                <div class="product-other-icon-{{ section.id }} icon-{{ block.settings.third_icon }}-{{ section.id }}">
                  {% if block.settings.third_icon == "yes" %}
                    {{ icon_yes }}
                  {% elsif block.settings.third_icon == "no"  %}
                    {{ icon_no }}
                  {% else %}
                    {{ icon_dont_know }}
                  {% endif %}
                </div>
              </div>
            {% endif %}
          </div>
        {% endfor %}
      </div>
    </div>
</div>
